INSERT INTO `bilgitbl` (`id`, `bilgibaslik`, `bilgi`) VALUES
(1, 'Hukuk Muhakemeleri Kanunu - 20', '<p>Madde Gerek&ccedil;esi:<br />\r\nBilindiği gibi, g&ouml;revsizlik veya yetkisizlik kararı &uuml;zerine yapılacak işlemler, 1086 sayılı Kanunun 193 &uuml;nc&uuml; maddesinde d&uuml;zenlenmiştir. Bu d&uuml;zenlemede, s&ouml;z&uuml; ge&ccedil;en Kanunun sisteminden kısmen ayrılınmıştır.<br />\r\n<br />\r\nMaddenin birinci fıkrasına g&ouml;re, davanın a&ccedil;ıldığı mahkeme, g&ouml;revsizlik veya yetkisizlik kararı verirse, bu karar (kanun yoluna t&acirc;bi olmadığı i&ccedil;in) kesinse bu tarihten, s&uuml;resi i&ccedil;inde kanun yoluna başvurulmayarak kesinleşmişse, s&uuml;renin dolmasından itibaren, kanun yoluna başvurulup, başvuru reddedilmişse, ret kararının tebliğinden itibaren, onbeş g&uuml;n i&ccedil;inde, taraflardan birinin kararı veren mahkemeye başvurarak, dosyanın g&ouml;revli veya yetkili mahkemeye g&ouml;nderilmesini istemesi gereklidir. Bu s&uuml;re i&ccedil;inde başvurulmayacak olursa, yine aynı mahkeme, davanın a&ccedil;ılmamış sayılmasına karar verecektir.&nbsp;<br />\r\n<br />\r\nG&ouml;r&uuml;ld&uuml;ğ&uuml; gibi, bu başvuru, g&ouml;revsizlik veya yetkisizlik kararı veren mahkemeye yapılmaktadır. Burada, g&ouml;revsizlik veya yetkisizlik kararında g&ouml;revli veya yetkili olduğu belirtilen mahkemeye başvuru kabul edilmeyerek, daha pratik bir yol oluşturulmak istenmiştir. Ger&ccedil;ekten, kendisinde dava a&ccedil;ılmamış veya dosya diğer mahkeme tarafından kendisine hen&uuml;z g&ouml;nderilmemiş bir mahkemeden davaya devamın istenmesi, pratik değildir. Mahkemenin, i&ccedil;inde bulunulan bu aşamada, yapabileceği tek şey, dosyayı g&ouml;revsizlik veya yetkisizlik kararı veren mahkemeden istemekten ibarettir. Oysa, g&ouml;revsizlik veya yetkisizlik kararı veren mahkemenin tarafların başvurusu &uuml;zerine, dosyayı g&ouml;revli veya yetkili mahkemeye g&ouml;ndermesi daha pratik bir yoldur.<br />\r\n<br />\r\nG&ouml;nderme işlemi sırasında, g&ouml;nderme masraflarının ve g&ouml;nderilen mahkemenin tebliğ giderlerinin varsa avanstan karşılanacağı, artan kısmın da dosya ile birlikte g&ouml;nderileceği, avans bu masrafı karşılamıyorsa g&ouml;nderme isteğinde bulunandan alınacağı tabiidir.<br />\r\n<br />\r\nİkinci fıkraya g&ouml;re ise g&ouml;revsizlik veya yetkisizlik kararı &uuml;zerine, g&ouml;revli veya yetkili olduğu belirtilerek dosya kendisine g&ouml;nderilen mahkeme, kendiliğinden taraflara bir davetiye g&ouml;ndererek davaya devam edilmesini sağlayacaktır.</p>\r\n'),
(2, 'Hukuk Muhakemeleri Kanunu - 19', '<p>Yetki itirazının ne zaman ve nasıl ileri s&uuml;r&uuml;leceği, yetkinin kesin olduğu ve kesin olmadığı h&acirc;ller ayırımı yapılarak d&uuml;zenlenmiştir.&nbsp;<br />\r\n<br />\r\nBirinci fıkraya g&ouml;re, kesin yetkinin s&ouml;z konusu olduğu h&acirc;llerde, mahkeme, bu husus bir dava şartı olarak kabul edildiğinden, yetkili olup olmadığını, davanın sonuna kadar, her zaman kendiliğinden araştıracaktır. Taraflar da mahkemenin yetkisiz olduğunu, yine davanın sona ermesine kadar, her zaman ileri s&uuml;rebileceklerdir. Esasen maddenin<br />\r\nbirinci fıkrasında yer alan bu durum, yetkiyi incelemesi i&ccedil;in mahkemeye bir hatırlatma niteliğindedir.<br />\r\n<br />\r\nİkinci fıkrada ise yetkinin kesin olmadığı h&acirc;llerde, yetki itirazının, cevap dilek&ccedil;esinde ileri s&uuml;r&uuml;lmesi esası kabul edilmiştir. Davalı, cevap dilek&ccedil;esinde yetki itirazında bulunmamışsa, cevap dilek&ccedil;esini verdikten sonra artık yetki itirazında bulunamayacaktır. Yetki itirazında bulunan taraf, yetkili mahkemeyi, birden fazla yetkili mahkemenin s&ouml;z konusu olduğu h&acirc;llerde ise kendi kanaatine g&ouml;re tercih ettiği yetkili mahkemeyi de itirazında belirtmek zorundadır.<br />\r\n<br />\r\n&Uuml;&ccedil;&uuml;nc&uuml; fıkraya g&ouml;re, mahkemenin yetkisizlik kararı verirken, yetkili mahkemeyi de g&ouml;stermesi gerekmektedir. Mahkeme, yetkisizlik kararı verirken, kesin yetkinin s&ouml;z konusu olduğu h&acirc;llerde, taraflar itirazlarında belirtmemiş olsalar bile, yetkili mahkemeyi kendisi bulup, yetkisizlik kararında g&ouml;stermelidir. Yetkinin kesin olmadığı h&acirc;llerde ise itirazda bulunan tarafın, itirazında belirttiği mahkeme ger&ccedil;ekten yetkili ise o mahkeme vereceği kararda yetkili mahkemeyi g&ouml;sterecektir.<br />\r\n<br />\r\nD&ouml;rd&uuml;nc&uuml; fıkrada, 1086 sayılı Kanunun 23 &uuml;nc&uuml; maddesinin ilk c&uuml;mlesine benzer şekilde, yetkinin kesin olmadığı davalarda, davalının s&uuml;resi i&ccedil;inde ve usul&uuml;ne uygun olarak yetki itirazında bulunmaması h&acirc;linde, davanın a&ccedil;ıldığı mahkemenin, yetkili h&acirc;le geleceği kabul edilmiştir.</p>\r\n'),
(3, 'Hukuk Muhakemeleri Kanunu - 17', '<p>Yetki s&ouml;zleşmesine ilişkin olarak yapılan d&uuml;zenlemede, tacirler veya kamu t&uuml;zel kişileri ile diğer kişiler, yetki s&ouml;zleşmesi yapmak a&ccedil;ısından, birbirinden ayırt edilmiştir. Tacirler veya kamu t&uuml;zel kişileri kendi aralarındaki hukuk&icirc; ilişkilerde her ikisi de hukuken eşit konumda sayılabilirler. Buna karşılık, tacirler veya kamu t&uuml;zel kişileri, diğer bir ger&ccedil;ek kişiye g&ouml;re, daha g&uuml;&ccedil;l&uuml; konumda bulunmaktadırlar. Daha zayıf konumda olan kimselerin daha g&uuml;&ccedil;l&uuml; olan tacir veya kamu t&uuml;zel kişilerine karşı, korunma ihtiyacı ortaya &ccedil;ıkmıştır. &Ouml;zellikle iltihak&icirc; s&ouml;zleşmelerle bu durum daha da belirginleşmektedir. T&uuml;keticiler, satıcı veya hizmet sunucu şirketlere karşı, hi&ccedil;bir pazarlık şansı olmaksızın, sadece kendilerine uzatılan s&ouml;zleşmeye imza atarak, şirket veya kamu t&uuml;zel kişisi tarafından konulan şartları, bu arada, yetki şartını da kabul etmek zorunda kalmaktadırlar. O nedenle, &ouml;rneğin Alman hukukunda, yetki s&ouml;zleşmesinin her durumda yapılması kabul edilmemektedir. Yetki s&ouml;zleşmesi yapılabilen alanlar olduk&ccedil;a sınırlandırılmış bulunmaktadır.<br />\r\n<br />\r\nTacirlerle kamu t&uuml;zel kişilerinin kendi aralarında, yapmış oldukları işlemlerde, belirtildiği gibi, daha zayıf konumda olan bir taraf yoktur. Kural olarak her iki tarafı da eşit kabul etmek m&uuml;mk&uuml;nd&uuml;r. Bu kimseler, kanunda belirtilen şartlara uygun olmak kaydı ile, aralarında yetki s&ouml;zleşmesi<br />\r\nyapabileceklerdir. Taraflar ayrıca, yapmış oldukları yetki s&ouml;zleşmesi ile, yetkili kıldıkları bir veya birden fazla mahkemenin yetkisinin, m&uuml;nhasır yetki olup olmadığını da kararlaştırabileceklerdir.<br />\r\n<br />\r\nBuna g&ouml;re, tacirlerin ve kamu t&uuml;zel kişilerinin, hukukumuzda tartışmalı olan, m&uuml;nhasır yetki s&ouml;zleşmesini de yapabilecekleri kabul edilmiştir. Taraflar, yetki s&ouml;zleşmesinde, aksini kararlaştırmadık&ccedil;a, dava sadece s&ouml;zleşmeyle belirlenen mahkemede veya mahkemelerde a&ccedil;ılabilecektir. Bu durumda, taraflarca aksi kararlaştırılmadık&ccedil;a, yapılan yetki s&ouml;zleşmesi, m&uuml;nhasır yetki s&ouml;zleşmesi şeklinde olacaktır. Taraflar, yetkili kıldıkları mahkemenin yanında, kanunen yetkili kılınan genel veya &ouml;zel yetkili mahkemelerin de yetkisinin devam etmesini istiyorlarsa, yani yetki s&ouml;zleşmesinin m&uuml;nhasır olmayan yetki s&ouml;zleşmesi şeklinde olmasını istiyorlarsa, bu durumun yetki s&ouml;zleşmesinde ayrıca belirtilmesi gerekecektir.<br />\r\n<br />\r\nTacirler veya kamu t&uuml;zel kişileri dışındaki diğer kişiler, &ouml;zellikle, tacir olmayan ger&ccedil;ek kişi t&uuml;keticiler, tacirler veya kamu t&uuml;zel kişilerine karşı hukuken daha zayıf durumdadır. O nedenle, bu kimselerin, daha g&uuml;&ccedil;l&uuml; olanlara karşı korunmaları gereklidir. Bu ama&ccedil;la, tacirler veya kamu t&uuml;zel kişileri ile bu nitelikte olmayan kimseler arasındaki yetki s&ouml;zleşmesine, &ouml;rneğin bir ger&ccedil;ek kişi t&uuml;ketici ile bir tacirin, yetki s&ouml;zleşmesi yapmalarına engel olunmak istenmiştir. Buna g&ouml;re, tacirler veya kamu t&uuml;zel kişileri ile bu nitelikte olmayan kimseler yetki s&ouml;zleşmesi yapamayacaklardır. Ayrıca bu kapsamda belirtmek gerekir ki, tacirler veya kamu t&uuml;zel kişileri dışındaki diğer kimselerin, kendi aralarında yetki s&ouml;zleşmesi yapmaları da kabul edilmemiştir.</p>\r\n');
INSERT INTO `bilgitbl` (`id`, `bilgibaslik`, `bilgi`) VALUES
(4, 'HMK 1', '<h2 style="font-style:italic;">4 Ekim 1927 tarihinde y&uuml;r&uuml;rl&uuml;ğe giren 1086 sayılı Hukuk Usul&uuml; Muhakemeleri Kanunu,<strong> İ</strong>svi&ccedil;re&rsquo;nin Neuch&acirc;t<em>el Kantonuna ait</em> 1925 tarihli Meden&icirc; Usul Kanunundan terc&uuml;me edilerek alınmıştır. Ancak Neuch&acirc;tel Meden&icirc; Usul Kanununun terc&uuml;me edilerek hukukumuza alınması sırasında, bazı b&ouml;l&uuml;mler i&ccedil;in Fransız ve Alman hukukundan da<ins><span class="marker"><tt> <code>yararlanıl</code>mıştır.</tt></span></ins><br />\r\n<br />\r\n1086 sayılı Kanun, y&uuml;r&uuml;rl&uuml;ğe girmesinden bug&uuml;ne kadar yaklaşık otuz kez değiştirilmiştir. <em>Bu değişikliklerin</em> t&uuml;m&uuml;nde, geciken yargının hızlanması, daha basit ve daha ucuz bir yargının sağlanması ama&ccedil;lanmıştır. Ancak bu değişiklikler genellikle, uygulamada g&ouml;r&uuml;len aksaklıkların ve şik&acirc;yet edilen hususların giderilmesine y&ouml;nelik belirli maddelerin veya belirli kurumların değiştirilmesi bi&ccedil;iminde yapılmıştır.<br />\r\n<br />\r\n1086 sayılı Kanunun y&uuml;r&uuml;rl&uuml;ğe girmesinden kısa bir s&uuml;re sonra, yeni bir Meden&icirc; Usul Kanunu yapılması i&ccedil;in &ccedil;eşitli tasarılar hazırlanmıştır. Bunlardan birincisi, 1946 yılında hazırlanan Tasarı olup kanunlaşması m&uuml;mk&uuml;n olmamıştır. Bu metin, 1952 yılında &ldquo;Hukukta Yargılama Usul&uuml; Kanunu Tasarısı&rdquo; olarak 1952 yılında T&uuml;rkiye B&uuml;y&uuml;k Millet Meclisine sunulmuş, ancak yine kanunlaşamamıştır. 1955 yılında birinci Tasarıdan yararlanarak hazırlanan &ldquo;Hukukta Muhakeme Usul&uuml; Kanunu Layihası&rdquo; T&uuml;rkiye B&uuml;y&uuml;k Millet Meclisine sevkedilmişse de burada g&ouml;r&uuml;ş&uuml;lemeden kad&uuml;k kalmıştır. Bu tasarıları 1967, 1971 ve 1993 yıllarında hazırlanan tasarılar takip etmiş, ancak bunlar da kanunlaşamamıştır. 1993 yılından sonra, yeni bir hukuk usul&uuml; muhakemesi tasarısı y&ouml;n&uuml;nde &ccedil;alışma yapılmamıştır.<br />\r\n<br />\r\nSon yıllarda temel kanunlar t&uuml;m olarak g&ouml;zden ge&ccedil;irilerek, yeni tasarılar hazırlanması yoluna gidilmiş ve bunun sonucunda hazırlanan T&uuml;rk Meden&icirc; Kanunu Tasarısı, 8/12/2001 tarihinde yayımlanarak, 1/1/2002 tarihinde y&uuml;r&uuml;rl&uuml;ğe girmiştir. Bor&ccedil;lar Kanunu ve Ticaret Kanunu hakkında oluşturulan komisyonlar tarafından hazırlanan tasarılar da tamamlanarak, kanunlaşmaları i&ccedil;in gerekli s&uuml;re&ccedil; başlatılmıştır. 21/6/2001 tarihinde y&uuml;r&uuml;rl&uuml;ğe giren Milletlerarası Tahkim Kanunu kabul edilirken, i&ccedil; hukuktaki tahkim kuralları Milletlerarası Tahkim Kanunu ile birlikte değerlendirilmediğinden, Uncitral Model Kanun h&uuml;k&uuml;mleri i&ccedil; tahkime alınamamış ve birbirinden ayrı tahkim h&uuml;k&uuml;mleri uygulamaya başlanmıştır. Halbuki Almanya, Hollanda ve İtalya&rsquo;da tahkim kuralları, mill&icirc; ve milletlerarası tahkim arasında bir ayırım yapılmaksızın tek bir kanun i&ccedil;inde d&uuml;zenlenmiştir. Bu gelişmelerin yanında, b&ouml;lge adliye mahkemelerinin kurulmasıyla ilgili, 5235 sayılı Adli Yargı İlk Derece Mahkemeleri ile B&ouml;lge Adliye Mahkemelerinin Kuruluş, G&ouml;rev ve Yetkileri Hakkında Kanunun kabul edilmesinden sonra, 1086 sayılı Kanunda kanun yollarıyla ilgili &ouml;nemli değişiklikler yapılmıştır. Bu gelişmeler, Hukuk Usul&uuml; Muhakemeleri Kanunu yerine yeni bir tasarı hazırlanmasını gerektirmiştir.<br />\r\n<br />\r\n1927 yılında y&uuml;r&uuml;rl&uuml;ğe giren 1086 sayılı Kanunun dili olduk&ccedil;a eskimiş ve gen&ccedil; hukuk&ccedil;ular tarafından anlaşılması zorlaşmıştır. Bunun yanında, Kanunun zaman i&ccedil;inde değiştirilmesi nedeniyle bazı kavram ve kurumlar ayrı terimle ifade edilmiştir. &Ouml;rneğin, 1086 sayılı Kanunun 9 uncu maddesinde &ldquo;yetki&rdquo;, 13 &uuml;nc&uuml; maddesinde ise &ldquo;sal&acirc;hiyet&rdquo;; 175 inci maddesinde &ldquo;tatil&rdquo;, 176 ncı maddede ise &ldquo;ara verme&rdquo;; ayrıca 176 ncı maddede &ldquo;karşılık dava&rdquo;, 203 &uuml;nc&uuml; maddede ise &ldquo;davayı m&uuml;tekabile&rdquo; ibareleri yer almaktadır. Yine 176 ncı maddede &ldquo;Yargıtay&rdquo;, 428 inci maddede ise &ldquo;Mahkemei Temyiz&rdquo; terimleri kullanılmaktadır. Benzer şekilde bilirkişi terimi, aynı zamanda &ldquo;ehlihibre&rdquo; ve &ldquo;ehlivukuf&rdquo; olarak ifade edilmiştir. B&ouml;ylelikle aynı kurum, aynı Kanun i&ccedil;inde birden fazla s&ouml;zc&uuml;kle ifade edilmeye başlanmıştır. Kanunun maddelerinde zaman i&ccedil;inde yapılan değişiklikler ve bazı maddelerin Kanundan &ccedil;ıkarılmasından sonra boşluklar oluşmuş, bazı yeni ilke ve kurumlar 1086 sayılı Kanunda yer almamıştır.<br />\r\n<br />\r\n469 sayılı Mehakimi Şer&rsquo;iyenin İlgasına ve Mehakim Teşkilatına Ait Ahkamı Muaddil Kanun ve 1086 sayılı kanunlarda toplu mahkeme olarak kurulması &ouml;ng&ouml;r&uuml;len asliye hukuk mahkemeleri, 5235 sayılı Kanunla yapılan değişikliğe kadar tek h&acirc;kimli mahkeme olarak g&ouml;rev yapmıştır. Ancak asliye hukuk mahkemelerinin 5235 sayılı Kanunla tek h&acirc;kimli mahkeme olarak kurulması &ouml;ng&ouml;r&uuml;ld&uuml;kten sonra, Kanundaki toplu mahkeme olarak &ouml;ng&ouml;r&uuml;len ifadelerin değiştirilmesi gerekmiştir. Nitekim 1086 sayılı Kanunda, toplu mahkemeler esas alınarak kullanılan &ldquo;tahkikat hakimi&rdquo;, &ldquo;reis&rdquo; gibi terimlerin, tek hakimli mahkeme esas alınarak d&uuml;zenlenmesi gerekmiştir.<br />\r\n<br />\r\nKanunun bazı maddelerinde ise yanlış kullanılan terimler vardır. &Ouml;rneğin 104 &uuml;nc&uuml; maddede &ldquo;istinabe&rdquo; yerine &ldquo;naip&rdquo;, 364 &uuml;nc&uuml; maddede ise &ldquo;naip&rdquo; yerine &ldquo;istinabe&rdquo;, 442 nci maddede &ldquo;h&uuml;k&uuml;m&rdquo; yerine &ldquo;il&acirc;m&rdquo; ve 109 uncu maddede &ldquo;m&uuml;ddei&rdquo; yerine &ldquo;m&uuml;ddi&rdquo; denilmiştir. Gıyap kurumu 1086 sayılı Kanundan &ccedil;ıkarıldığı halde, gıyap s&ouml;zc&uuml;kleri Kanunun 323, 364 ve 509 uncu maddelerinde h&acirc;l&acirc; yer almaktadır.<br />\r\n<br />\r\nYeni bir Tasarı hazırlanmasının nedenlerinden birisi de &uuml;lkemizdeki adil yargılanma hakkının sağlanabilmesidir. Avrupa İnsan Hakları S&ouml;zleşmesi ile taahh&uuml;t edilen adil yargılanma hakkının sağlanabilmesi i&ccedil;in, usul kanunlarındaki h&uuml;k&uuml;mler uygun h&acirc;le getirilmelidir. &Ccedil;&uuml;nk&uuml; adil yargılanma hakkı, yargılamanın makul s&uuml;re i&ccedil;inde tamamlanmasını gerektirmektedir. Makul s&uuml;re i&ccedil;inde yargılama yapılabilmesi i&ccedil;in, 1086 sayılı Kanunda yer alan ve engel teşkil eden h&uuml;k&uuml;mlerin değiştirilmesi gerekmiştir. Makul s&uuml;re i&ccedil;inde yargılama yapılabilmesinde usul kanunlarının &ouml;nemli rol&uuml; bulunmaktadır. Ancak Hukuk Usul&uuml; Muhakemeleri Kanunu tek başına yargının hızlanmasını sağlamaya yeterli değildir. Nitekim, bunun b&ouml;yle olmadığı şimdiye kadar yapılan değişikliklerde de g&ouml;r&uuml;lm&uuml;şt&uuml;r. Yargının diğer sorunları &ccedil;&ouml;z&uuml;mlenmedik&ccedil;e hazırlanan Tasarının tek başına gecikmeleri &ouml;nlemesi m&uuml;mk&uuml;n olmayacaktır.<br />\r\n<br />\r\nBunların yanında Kanunumuzun mehazını teşkil eden Neuch&acirc;tel Medeni Usul Kanunu da değişmiştir. Neuch&acirc;tel Medeni Usul Kanunu i&ccedil;in hazırlanan Kanun Tasarısı 28 Haziran 1988 tarihinde Kanton Parlamentosunun Adalet Komisyonuna g&ouml;nderilmiş, 30 Eyl&uuml;l 1991 tarihinde oybirliği ile kabul edilerek 1 Nisan 1992 tarihinde y&uuml;r&uuml;rl&uuml;ğe girmiş ve b&ouml;ylelikle 7 Nisan 1925 tarihli mehaz Kanunumuz y&uuml;r&uuml;rl&uuml;kten kaldırılmıştır. Yeni Kanunda eski Kanunun sistematiği ve kurumları korunmakla beraber &ouml;nemli değişiklikler yapılmıştır.<br />\r\n<br />\r\nYukarıda a&ccedil;ıklanan nedenlerle Hukuk Usul&uuml; Muhakemeleri Kanununun g&ouml;zden ge&ccedil;irilerek yeni bir tasarı hazırlanması i&ccedil;in, 2992 sayılı Kanunun 34 &uuml;nc&uuml; maddesi h&uuml;km&uuml; uyarınca, 27/1/2004 tarihli Bakan Olur&rsquo;u ile bir Komisyon oluşturulmuştur. Komisyonun oluşturulmasındaki ama&ccedil;, Bakanlık Olurunda; &ldquo;Yargı reformu &ccedil;er&ccedil;evesinde olmak &uuml;zere; Hukuk Usul&uuml; Muhakemeleri Kanununda uygulamadan kaynaklanan aksaklıkları gidermek, davaların hızlı, basit, en az giderle ve etkin bir bi&ccedil;imde g&ouml;r&uuml;lmesi ve bir kısım uyuşmazlıkların nizasız kaza, sulh, uzlaşma gibi y&ouml;ntemlerle &ccedil;&ouml;z&uuml;m&uuml;n&uuml; sağlamak, karşılaştırmalı hukuktaki gelişmeleri hukuk yargılamasına yansıtmak amacıyla yeni bir Kanun Tasarısı hazırlamak.&rdquo; şeklinde belirtilmiştir.<br />\r\nTasarı, bu &ccedil;er&ccedil;evede oluşturulan Komisyon tarafından hazırlanmıştır.&nbsp;<br />\r\n<br />\r\nKomisyon ilk olarak hazırlanacak Tasarı ile ilgili bazı ilke ve hedefler belirlemiş ve &ccedil;alışmalarını bu &ccedil;er&ccedil;evede y&uuml;r&uuml;tm&uuml;şt&uuml;r.<br />\r\n<br />\r\nBu ilkelerden birincisi, yepyeni bir tasarı hazırlamak yerine, mevcut 1086 sayılı Kanunun temel felsefesi korunup, mevcut h&uuml;k&uuml;mleri değerlendirilerek tasarının hazırlanmasıdır. Zira, 1086 sayılı Kanunun kabul&uuml;nden bug&uuml;ne kadar yaklaşık 80 yıllık bir yargılama hukuku tecr&uuml;besi ve k&uuml;lt&uuml;r&uuml; oluşmuştur. Bu s&uuml;re i&ccedil;inde, doktrinde bir &ccedil;ok kitap, tez ve makaleler yazılmış, farklı g&ouml;r&uuml;şler tartışılmış, yargı kararlarıyla ortaya &ccedil;ıkan ciddi bir i&ccedil;tihat birikimi ortaya &ccedil;ıkmıştır. T&uuml;m bu birikimin yok sayılmasının doğru olmayacağı d&uuml;ş&uuml;ncesi ile yeni bir yargılama sistemi yerine, mevcut yargılama hukuku kurallarının g&ouml;zden ge&ccedil;irilmesi, aksayan y&ouml;nlerinin tespit edilerek d&uuml;zeltilmesi, ge&ccedil;en s&uuml;re i&ccedil;inde eskiyen, g&uuml;ncelliğini kaybeden, ihtiyacı karşılamayan kurumların yenilenmesi, karşılaştırmalı hukuk &ccedil;er&ccedil;evesinde yeni kurum ve kuralların kabul edilmesi, uygulamada ortaya &ccedil;ıkan sorunlara, temel kavram ve kurumları zedelemeden cevap verecek yeni d&uuml;zenlemelerin kabul edilmesi ilkesi benimsenmiştir. Ancak gerektiği zaman, mevcut sistemle uyum g&ouml;zetilerek yeni kurum ve kurallara da yer verilmiştir. Bu a&ccedil;ıdan, Tasarı aslında 1086 sayılı Kanunun revize edilmesidir. Yeni bir usul kanunu sebebiyle uygulamada s&uuml;rebilecek teredd&uuml;t ve i&ccedil;tihat uyuşmazlıkları da b&ouml;ylelikle &ouml;nlenmek istenmiştir.<br />\r\n<br />\r\nİkinci olarak, son hazırlanan tasarılarda &ouml;nemli bir tartışma konusu olan, sulh hukuk ve asliye hukuk mahkemesi ayırımı muhafaza edilmiştir. Bu ayırımın kaldırılması i&ccedil;in ileri s&uuml;r&uuml;len, g&ouml;revsizlik kararı verilmemesi nedeniyle yargılamanın hızlanacağı gerek&ccedil;esi Komisyon tarafından kabul edilmemiştir. &Ccedil;&uuml;nk&uuml; bu ayırımın tarihi nedenleri yanında, uygulanan yargılama usul&uuml; ve bu mahkemelerden verilen kararlara karşı kanun yollarına başvuru olanaklarının farklı oluşu, bu ayırımın korunmasını zorunlu kılmıştır. Ayrıca bu mahkemeler arasında miktar ve değerine bakılmaksızın a&ccedil;ılan davalar bakımından sağlanan bir uzmanlık da korunmuştur. &Ouml;rneğin, ifl&acirc;s davaları ile vakfa ilişkin davaların asliye hukuk mahkemesinde; buna karşılık kira s&ouml;zleşmesinden doğan uyuşmazlıkların sulh hukuk mahkemesinde g&ouml;r&uuml;lmesi s&uuml;rd&uuml;r&uuml;lm&uuml;şt&uuml;r. Ayrıca, başka mahkemelerin g&ouml;revine girmeyen t&uuml;m &ccedil;ekişmesiz yargı işlerinin de sulh hukuk mahkemesinde bakılması kabul edilmiştir. Bu yapılırken yazılı yargılama usul&uuml; ile basit yargılama usul&uuml;n&uuml;n birbirinden daha farklı usuller i&ccedil;ermesi dikkate alınmıştır. Sulh hukuk mahkemelerinde a&ccedil;ılan davaların daha basit ve daha kısa s&uuml;rede &ccedil;&ouml;z&uuml;m&uuml; ama&ccedil;lanmıştır.<br />\r\n<br />\r\nTasarıda g&uuml;ncel ve akıcı bir dil kullanılması ilkesi benimsenmiştir. Ancak, &ouml;zellikle hukuk&icirc; kavramlar konusunda, &uuml;zerinde teredd&uuml;t edilmeyen, yerleşmiş, herkesin anladığı kavram ve terimler aynen muhafaza edilmiştir. T&uuml;rk Meden&icirc; Kanununun, &ouml;zel hukuk alanında temel Kanun olması dikkate alınarak, m&uuml;mk&uuml;n olduğunca, bu Kanunun kabul ettiği kavramlar ve terimler &ouml;ncelikle tercih edilmiştir. Belirli bir alanda uygulaması olan kavram ve kurumların, o alanda yerleşmiş kavram ve terimlerin kullanılmasına &ouml;zen g&ouml;sterilmiştir. Kavram, kurum ve terim bakımından karışıklığa meydan vermemek i&ccedil;in, m&uuml;mk&uuml;n olduğunca teknik kavramlarda aynı terimler kullanılmaya &ccedil;alışılmıştır. Esas olarak doktrin ve uygulamada genel kabul g&ouml;ren terimler kullanılmaya &ccedil;alışılmıştır.<br />\r\n<br />\r\nGenel kabul g&ouml;rmeyen terimlere Tasarıda yer verilmemesine &ouml;zen g&ouml;sterilmiştir. &Ouml;rneğin &ldquo;bağımlı katılan&rdquo; yerine, &ldquo;fer&rsquo;&icirc; m&uuml;dahil&rdquo;, &ldquo;bağımsız katılan&rdquo; yerine, &ldquo;asl&icirc; m&uuml;dahale&rdquo; terimleri kullanılmıştır.<br />\r\n<br />\r\nTasarı hazırlanırken, 1086 sayılı Kanunun madde numaraları muhafaza edilememiştir. &Ccedil;&uuml;nk&uuml; yeni hazırlanan bir tasarıda getirilen yeniliklere, Kanundan &ccedil;ıkarılan yaklaşık seksen maddenin boşlukları i&ccedil;inde yer vermek ve b&ouml;ylece madde numaraları değişmeden d&uuml;zenleme yapabilmek m&uuml;mk&uuml;n değildir.&nbsp;<br />\r\n<br />\r\n10/10/1984 tarihli ve 3056 sayılı Kanuna dayanılarak &ccedil;ıkartılan Mevzuat Hazırlama Usul ve Esasları Hakkında Y&ouml;netmeliğin 13 &uuml;nc&uuml; maddesinde, maddelerin nasıl yazılacağı a&ccedil;ıklanmıştır. Bu d&uuml;zenlemeye g&ouml;re, maddelerin tesels&uuml;l etmesi ve hazırlanan tasarılarda madde boşluklarının bulunmaması gerekmektedir. Yine bu Y&ouml;netmeliğe g&ouml;re hazırlanan Tasarıda madde başlıkları yer almalıdır. Hukuk Usul&uuml; Muhakemeleri Kanunumuzun maddelerinde madde başlıkları yoktur. Ancak 5236 sayılı Kanunla 1086 sayılı Kanuna eklenen 426/A-439 uncu maddelerde madde başlıkları yer almaktadır. Tasarıda şekil olarak bir farklılık da maddelerin fıkralarının parantez i&ccedil;inde g&ouml;sterilmiş olmasıdır. Bu yenilik h&uuml;k&uuml;mlerin takibinde b&uuml;y&uuml;k kolaylık sağlamaktadır.<br />\r\n<br />\r\nHazırlanan Tasarıda, ayrıntılı d&uuml;zenleme yapılmasından ka&ccedil;ınılmıştır. Her kurumla ilgili temel esasların kanunda teredd&uuml;ts&uuml;z yer alması ve uygulamada sorun oluşturacak hususların a&ccedil;ıklığa kavuşturulması kabul edilmiştir. Bunun yanında, zorunlu olmadık&ccedil;a kazuistik bir y&ouml;ntem izlenmekten ka&ccedil;ınılmıştır. Maddelerin d&uuml;zenlenmesinde &ldquo;&ouml;zel h&uuml;k&uuml;mlerin saklı tutulması&rdquo; şeklindeki ifadelerden ka&ccedil;ınılmış, zorunlu olmadık&ccedil;a bu atıf y&ouml;ntemi benimsenmemiştir. &Ccedil;&uuml;nk&uuml;, &ouml;zel h&uuml;k&uuml;mler zaten saklı ve &ouml;nceliklidir. Ancak, &ouml;zellikle vurgulanması gereken yerlerde genel ifadelerle değil, ilgili kurum belirtilerek atıf yapılmıştır. Bu atıflar bakımından da maddelerde ilerde yapılacak değişiklikler sebebiyle bir karışıklık &ccedil;ıkmaması i&ccedil;in, madde atfından daha &ccedil;ok, kuruma veya kurala atıf y&ouml;ntemi kabul edilmiştir.<br />\r\n<br />\r\nKanun sistematiği bakımından, yargılama akışına uygun, takip edilmesi kolay, mantık&icirc;, sade ve basit bir sistematik anlayış benimsenmiştir. Yargılamanın daha doğru, amacına uygun, daha hızlı ve etkin y&uuml;r&uuml;t&uuml;lmesine hizmet edecek yeni kurumların alınması yanında, yargılama sisteminin daha basitleştirilmesi de kabul edilmiştir. Ancak usul hukukunun amacı olan madd&icirc; ger&ccedil;eğe ulaşmaktan &ouml;d&uuml;n verilmeyecek bir d&uuml;zenleme yapılmaya &ccedil;alışılmıştır.<br />\r\n<br />\r\nTasarıda, mahkeme dışı &ccedil;&ouml;z&uuml;m yolları ile sulh veya uzlaşmayı m&uuml;mk&uuml;n kılacak ve teşvik edecek bir alt yapının oluşturulması benimsenmiştir. Uygulamada zaman zaman yanlışlıklara sebep olan, Kanunda bir &ccedil;ok farklı s&uuml;renin d&uuml;zenlenmesi yerine, temel bazı s&uuml;relerin kabul edilerek, bunlara uygun h&uuml;k&uuml;mlere yer verilmesi d&uuml;ş&uuml;n&uuml;lm&uuml;şt&uuml;r.<br />\r\n<br />\r\nTasarıda, yargılamanın niteliği ile ters d&uuml;şmediği &ouml;l&ccedil;&uuml;de g&uuml;ncel teknik gelişme ve kolaylıklardan yararlanılması, &ouml;rneğin ses ve g&ouml;r&uuml;nt&uuml; kayıtlarının yargılamada kullanılması benimsenmiş, deliller bakımından teknik gelişmelerin g&ouml;zetilmesi d&uuml;ş&uuml;n&uuml;lm&uuml;şt&uuml;r. Bu konuda yeni, fakat yargılama ilkelerine ters d&uuml;şmeyen en az riskli y&ouml;ntem kabul edilmiştir.<br />\r\n<br />\r\nT&uuml;m h&uuml;k&uuml;mlerin d&uuml;zenlenmesinde, tarafların ve ilgililerin hak arama &ouml;zg&uuml;rl&uuml;ğ&uuml;n&uuml; genişleten, yargılama sırasındaki haklarını en iyi şekilde teminat altına alacak ve yargı organlarının y&uuml;celiği ve saygınlığını koruyacak ilkelere yer verilmiştir.<br />\r\n<br />\r\nBug&uuml;ne kadar &ccedil;ekişmesiz yargı işleri, farklı kanunlarda, dağınık şekilde ve yetersiz h&uuml;k&uuml;mlerle y&uuml;r&uuml;t&uuml;lm&uuml;şt&uuml;r. &Ccedil;ekişmesiz yargıyla ilgili bazı yabancı &uuml;lkelerde olduğu gibi ayrı bir &ccedil;ekişmesiz yargı kanunu yapılması yerine, İsvi&ccedil;re Federal Meden&icirc; Usul Kanunu Taslağındaki gibi, Tasarının i&ccedil;inde d&uuml;zenleme yapılmasının daha uygun olacağı kanaatine varılmış ve &ccedil;ekişmesiz yargı da d&uuml;zenlenmiştir. Buna karşılık yargının y&uuml;k&uuml;n&uuml;n azaltılması yollarından birisi olan alternatif uyuşmazlık &ccedil;&ouml;z&uuml;mleri hakkında Tasarıda d&uuml;zenleme yapılmamıştır. Sadece &ldquo;&Ouml;n İnceleme&rdquo; B&ouml;l&uuml;m&uuml;nde h&acirc;kimin tarafları bu yola teşvik edebilmesine imk&acirc;n sağlanmıştır. Avrupa Birliğine dahil olan &uuml;lkelerdeki gibi, alternatif uyuşmazlık &ccedil;&ouml;z&uuml;mleri hakkında ayrı bir kanun hazırlanmasının daha uygun olacağı kabul edilmiştir.<br />\r\n<br />\r\nTasarının madde gerek&ccedil;elerinde, değişikliklerin gerek&ccedil;esi yer aldığı i&ccedil;in burada sadece &ccedil;ok genel olarak getirilen yenilikler değerlendirilecektir.<br />\r\n<br />\r\nTasarının Birinci Kısmı Genel H&uuml;k&uuml;mler başlığı altındaki, Birinci b&ouml;l&uuml;mde, G&ouml;rev, Yetki ve Yargı Yeri Belirtilmesi incelenmiştir. Bu B&ouml;l&uuml;m&uuml;n Birinci Ayırımda g&ouml;rev d&uuml;zenlenmiştir. G&ouml;revle ilgili yenilik yapılmamış sadece maddelerde teredd&uuml;t edilen hususlarda a&ccedil;ıklık getirilmiş veya uygulamada kısm&icirc; dava a&ccedil;ılmasında g&ouml;revin belirlenmesindeki gibi bazı zorluklar aşılmaya &ccedil;alışılmıştır. Nitekim, kısm&icirc; dava a&ccedil;an kişinin saklı tuttuğu kısmın &ccedil;ekişmeli olup olmadığını &ccedil;oğu zaman bilmesi m&uuml;mk&uuml;n olmadığından maddede daha basit bir d&uuml;zenleme yapılmış ve alacağın saklı tutulan kısmı da nazara alınarak g&ouml;revli mahkemenin belirlenmesi kabul edilmiştir.<br />\r\n<br />\r\nSulh hukuk mahkemelerinin g&ouml;revi, 1086 sayılı Kanun ile Tasarıda 8 inci maddede d&uuml;zenlenmiştir. Farklı olarak ifl&acirc;s davaları dışında konkordato veya uzlaşma suretiyle sermaye şirketlerinin yeniden yapılandırılması sulh hukuk mahkemelerinin g&ouml;revi dışında tutulmuştur.<br />\r\n<br />\r\nYetki konusunda daha sistematik, daha a&ccedil;ık bir d&uuml;zenleme yapılmaya &ccedil;alışılmıştır. Bu Kısımda yeni olarak, yetki s&ouml;zleşmelerinin sadece tacirler ve kamu t&uuml;zel kişileri arasında yapılması kabul edilmiştir. &Ccedil;&uuml;nk&uuml; yetki s&ouml;zleşmeleri ile genellikle g&uuml;&ccedil;l&uuml; olan taraf, yetkili olmayan ve dilediği bir mahkemeyi yetkili kılabilmektedir. Usul hukukunun sosyal y&ouml;n&uuml; g&uuml;&ccedil;s&uuml;z olan kişileri yargılama sırasında korumayı gerektirmektedir. Bu nedenle Tasarıda, tacir olmayanlar bakımından yetki s&ouml;zleşmesi yapılabilmesi olanağı kaldırılmıştır. Yeni getirilen h&uuml;k&uuml;mlere g&ouml;re, tacirler ve kamu t&uuml;zel kişileri, aralarında doğmuş veya doğabilecek uyuşmazlıklar hakkında bir veya birden fazla mahkemeyi yetkili kılabileceklerdir. Yine taraflarca aksi kararlaştırılmadık&ccedil;a dava sadece s&ouml;zleşmeyle belirlenen mahkemelerde a&ccedil;ılabilecektir. B&ouml;ylelikle olumsuz yetki s&ouml;zleşmesi yapılabilmesi tacirler veya kamu t&uuml;zel kişileri bakımından kabul edilmiştir.<br />\r\n<br />\r\nBirinci Kısmın &Uuml;&ccedil;&uuml;nc&uuml; B&ouml;l&uuml;m&uuml;n&uuml;n İkinci Ayırımında, h&acirc;kimlerin sorumluluğu d&uuml;zenlenmiş ve h&acirc;kimlerin fiillerinden dolayı Devletin birinci derecede sorumluluğu kabul edilmiştir. B&ouml;ylelikle Anayasamızın 129 uncu maddesinin beşinci fıkrasına uygun bir d&uuml;zenleme yapılmıştır.&nbsp;<br />\r\nBu Kısmın D&ouml;rd&uuml;nc&uuml; B&ouml;l&uuml;m&uuml;nde, taraflar ve davaya katılan &uuml;&ccedil;&uuml;nc&uuml; kişiler d&uuml;zenlenmiştir. Asl&icirc; m&uuml;dahale de davaya m&uuml;dahale maddeleri arasında a&ccedil;ık&ccedil;a d&uuml;zenlenmiştir.<br />\r\nİkinci Kısımda d&uuml;zenlenen dava &ccedil;eşitleri ve dava şartları ile ilgili h&uuml;k&uuml;mler yenidir. Eda, tespit ve inşa&icirc; davalar dışında, kısm&icirc; dava, davaların yığılması, terditli dava, se&ccedil;imlik dava ve topluluk davası yeni olarak d&uuml;zenlenmiştir. Topluluk davası ile derneklerin &uuml;yeleri adına tespit davası a&ccedil;abilmeleri ya da m&uuml;dahalenin &ouml;nlenmesini isteyebilmeleri m&uuml;mk&uuml;n h&acirc;le getirilmiştir. &Ouml;rneğin, bir t&uuml;ketici derneği, t&uuml;keticilerle ilgili dava a&ccedil;abilecektir. Bu Kısmın İkinci B&ouml;l&uuml;m&uuml;nde dava şartları genel olarak sayılmış ve ayrıca bu dava şartlarının incelenmesi ele alınmıştır. Derdestlik dava şartı h&acirc;line getirilerek, aynı davanın ikinci kez a&ccedil;ılması durumunda derdestliğin ilk itiraz olarak ileri s&uuml;r&uuml;l&uuml;p her iki davanın gereksiz yere g&ouml;r&uuml;lmesi &ouml;nlenmek istenmiştir. Bu sayede hem gereksiz masraf yapılması &ouml;nlenecek hem de mahkemeler gereksiz yere meşgul edilmeyecektir. Bu Kısmın İkinci B&ouml;l&uuml;m&uuml;n&uuml;n İkinci Ayırımında ilk itirazlar d&uuml;zenlenmiştir. İlk itirazların sayısı azalmıştır. Dava ve cevap dilek&ccedil;esindeki eksikliklerin ilk itiraz olarak ileri s&uuml;r&uuml;lmesi kaldırılmıştır. Zira bu dilek&ccedil;elerdeki zorunlu unsurların ilk itiraz olarak ileri s&uuml;r&uuml;lmese bile her zaman ileri s&uuml;r&uuml;lebilmesi m&uuml;mk&uuml;n idi.<br />\r\nBunun gibi davaların birleştirilmesi de ilk itiraz olarak kabul edilmemiştir. &Ccedil;&uuml;nk&uuml;, ilk itiraz olarak ileri s&uuml;r&uuml;lmediği takdirde ayrı mahkemelerde g&ouml;r&uuml;len davaların birleştirilmesi m&uuml;mk&uuml;n değildir. Bu ise uygulamada davaların daha da gecikmesine neden olan bir davanın diğeri i&ccedil;in bekletilerek, yani bekletici sorun sayılarak uzamasına neden oluyordu.&nbsp;<br />\r\n<br />\r\n&Uuml;&ccedil;&uuml;nc&uuml; Kısım olan Yazılı Yargılama Usul&uuml; Kısmının Beşinci B&ouml;l&uuml;m&uuml;n&uuml;n &Uuml;&ccedil;&uuml;nc&uuml; Ayrımında ise &ouml;n sorun ve bekletici sorun d&uuml;zenlenmiştir. &Ouml;n sorun 1086 sayılı Kanunda &ldquo;hadise&rdquo; olarak d&uuml;zenlendiği h&acirc;lde bekletici sorunla ilgili h&uuml;k&uuml;m yenidir.<br />\r\n<br />\r\n&Uuml;&ccedil;&uuml;nc&uuml; Kısımda, Yazılı Yargılama Usul&uuml;ne ilişkin h&uuml;k&uuml;mler yer almaktadır. Burada en &ouml;nemli yenilik davanın a&ccedil;ılmasıyla davacıdan avans alınmasıdır. B&ouml;ylelikle dava sırasında her işlem i&ccedil;in ayrı ayrı masraf alınması sebebiyle zaman kaybı &ouml;nlenmek istenmiştir. Davanın başında avans alınması bu gecikmeleri &ouml;nleyecek niteliktedir. Yargılama giderlerinin zamanında yatırılmaması, tek başına davaların gecikme sebebi olmamakla birlikte, &ouml;nemli etkenlerden biridir. Gerek kanundan gerekse yerleşmiş yargı uygulamalarından kaynaklanan bu aksaklığın &ouml;nlenebilmesi amacıyla, &ouml;ncelikle dava a&ccedil;ılmasıyla ilgili &ldquo;Har&ccedil; ve avans &ouml;denmesi&rdquo; kenar başlıklı 125 inci madde d&uuml;zenlenmiştir. Bu h&uuml;k&uuml;m, davacının dilek&ccedil;esini mahkemeye sunarken, a&ccedil;tığı davadan kaynaklanan yargılama giderlerinin yaklaşık olarak mahkemece hesaplanarak kendisinden peşin olarak tahsilini &ouml;ng&ouml;rmektedir. Yine hangi masrafların yargılama giderleri kapsamına girdiği belirlenmiş, ayrıca davacının davayı a&ccedil;arken &ouml;demek zorunda olduğu giderin peşin olarak tahsilinden sonra, taraflardan her birinin ikamesini talep ettiği delil i&ccedil;in belirlenen avansı, verilen kesin s&uuml;re i&ccedil;inde yatırma zorunluluğu getirilmiştir.<br />\r\n<br />\r\nUygulamada kabul edilmemekle beraber, doktrinde genel kabul g&ouml;ren irad&icirc; taraf değişikliği Tasarıda kabul edilmiştir.<br />\r\n<br />\r\n&Uuml;&ccedil;&uuml;nc&uuml; Kısmın D&ouml;rd&uuml;nc&uuml; B&ouml;l&uuml;m&uuml;nde &Ouml;n İnceleme d&uuml;zenlenmiştir. &ldquo;&Ouml;n İnceleme&rdquo; başlığını taşıyan bu B&ouml;l&uuml;m, meden&icirc; usul hukuku alanındaki kanunlaştırmada, hukukumuz i&ccedil;in &ouml;nemli bir yeniliktir.&nbsp;<br />\r\n<br />\r\nUygulamada en &ccedil;ok şik&acirc;yet edilen ve yargılamanın gecikmesi sebebi olarak g&ouml;sterilen &ouml;nemli bir husus, tam hazırlık yapılmadan tahkikata başlanmasıdır. Hatta &ccedil;oğu kez, taraflar delillerini tam olarak g&ouml;stermeden ve dilek&ccedil;elerine eklemeden, mahkeme de delilleri tam olarak toplamadan tahkikata başlamaktadır. Delillerin ve hukuk&icirc; uyuşmazlığın tartışılması i&ccedil;in yeterli hazırlık yapılmadan tahkikata başlandığında, istenen sonu&ccedil; elde edilememekte, tahkikat, delillerin toplanması i&ccedil;in gereksiz yere uzamaktadır. Bunun yanında, mahkemelerce, yeterli hazırlık yapılıp dosyalar incelenmediğinden ve taraflar da bu konuda gerekli a&ccedil;ıklamaları tam olarak yapmadıklarından, yargılamanın başında karar verilmesi gereken, usule ilişkin dava şartları ve ilk itirazlar davanın sonunda karar verilebilmektedir. &Ouml;rneğin, iki yıl s&uuml;ren bir davanın sonunda, bazen mahkemenin g&ouml;revsizlik ya da yetkisizlik kararı verdiği veya zamanaşımı gerek&ccedil;esiyle davayı reddettiği g&ouml;r&uuml;lebilmektedir. Dilek&ccedil;elerin karşılıklı verilmesi aşaması tamamlandıktan ve tarafların elinde bulunan delillerin mahkemeye sunulmasından, elde olmayanların ise masrafları &ouml;dendikten ve nereden getirtileceğine ilişkin bilgiler verildikten sonra, tahkikatın başlatılması ama&ccedil;lanmıştır. Buna bağlı olarak, tahkikatın, amacına uygun olarak, sadece uyuşmazlığın ve uyuşmazlığa ilişkin delillerin incelendiği ve tartışıldığı bir aşama h&acirc;line getirilmesi; bunun i&ccedil;in de tarafların anlaştıkları ve anlaşamadıkları hususların tahkikat başlamadan tam tespiti gereklidir. Ayrıca, işin esasına girilmesi gerekli olmayan, usule ilişkin kararlarla s&uuml;relere ilişkin kararların da başlangı&ccedil;ta verilmesi, bir &ccedil;ok uyuşmazlığın baştan h&acirc;lli sonucunu doğuracaktır. Bu aşamada, uyuşmazlık konuları tam ve a&ccedil;ık olarak tespit edilmiş olacağından, tarafların durumlarını g&ouml;zden ge&ccedil;irerek sulh olmaları da daha kolay h&acirc;le gelebilecektir. Bu aşamanın diğer bir olumlu y&ouml;n&uuml; ise uyuşmazlık noktaları kesin olarak tespit edildiğinden, inceleme ve hukuk&icirc; tartışma da bu noktalara m&uuml;nhasır olarak yapılacak, gereksiz işlemlerin ve zaman kaybının &ouml;n&uuml;ne ge&ccedil;ilmiş olacaktır.<br />\r\n<br />\r\n&Ouml;n incelemenin amacına uygun yapılması, taraflar arasında sulh imk&acirc;nını artıracak, hatta yargılama dışı uyuşmazlık &ccedil;&ouml;z&uuml;m yollarıyla da bağlantılı olarak tarafların doğrudan ya da arabulucu yardımıyla uyuşmazlığı &ccedil;&ouml;zmelerinin yolunu a&ccedil;acaktır. Taraflar sulh olmadıklarında ise tahkikat i&ccedil;in t&uuml;m hazırlıklar yapılmış olduğundan, tahkikatta sadece tanık dinlenmesi, bilirkişiye başvurulması ve keşif gibi usul işlemleri ger&ccedil;ekleştirilecek, tahkikatın arka arkaya yapılacak birka&ccedil; oturumda da tamamlanması m&uuml;mk&uuml;n olabilecektir. Bu B&ouml;l&uuml;m&uuml;n &ldquo;&Ouml;n İnceleme&rdquo; olarak ifade edilmesinin sebebi, dilek&ccedil;elerden sonra, mahkemece yapılacak ilk inceleme aşaması olması, ancak işin hen&uuml;z esasına tam olarak girilmemesidir. Bu aşama, tahkikat olmadığından tahkikat ifadesi de kullanılmamıştır. &Ccedil;&uuml;nk&uuml;, bu aşamada bir &ouml;n tahkikat ve benzeri işlemlerden daha &ccedil;ok, tahkikattan &ouml;nce yapılması gereken işlemler tamamlanmaktadır. Bu B&ouml;l&uuml;m, Tasarıda yeni olmakla birlikte, hukukumuza tamamen yabancı da değildir. Nitekim istinaf aşamasında &ouml;n inceleme kabul edilmiştir. Yine temyiz aşamasında da işin esasına girilmeden &ouml;n inceleme yapılmaktadır.<br />\r\n<br />\r\nD&ouml;rd&uuml;nc&uuml; Kısım &ldquo;İspat ve Deliller&rdquo; başlığını taşımaktadır. Bu Kısımdaki ispat hakkı, ispat y&uuml;k&uuml; ve karşı ispatla ilgili h&uuml;k&uuml;mler yenidir. D&ouml;rd&uuml;nc&uuml; Kısmın İkinci B&ouml;l&uuml;m&uuml;nde &ldquo;senet&rdquo; yerine &ldquo;belge ve senet&rdquo; ibaresine yer verilmiştir. Bu B&ouml;l&uuml;m i&ccedil;inde delil olarak, belge ve senet d&uuml;zenlenmiştir.<br />\r\nBurada ilk defa hukukumuzda belge kavramı ile senet kavramı birbirinden ayrılmış, belge, senet kavramını da i&ccedil;ine alacak şekilde bir &uuml;st kavram olarak kabul edilmiştir. Belge ile senedin ayrılması hem kavramsal hem de hukuk&icirc; anlamda &ouml;nemlidir. Her şeyden &ouml;nce, belge genel anlamda bir bilgiyi taşımaya elverişli ortamı ya da şeyi ifade etmektedir. Ş&uuml;phesiz her bilgi taşıyıcının delil olarak bir değeri olmakla birlikte, o bilginin ne kadar g&uuml;venli taşındığı ve kesinliği ile bağlantılı şekilde delil olarak kullanılırken farklı kuvvette kabul edilmektedir. Bu sebeple &ouml;rneğin, altı imzalanmış k&acirc;ğıt &uuml;st&uuml;ndeki bir metin, yazı veya imzası ink&acirc;r edilmedik&ccedil;e senet olarak kesin delil niteliğine sahiptir.<br />\r\nG&uuml;venli elektronik imza ile oluşturulan elektronik ortamdaki veriler ise doğrudan senet olmamakla birlikte, senet kuvvetindedir. Oysa, imzasız sadece i&ccedil;inde belirli bilgiyi taşıyan bir k&acirc;ğıt &uuml;zerindeki yazı, yazılı delil başlangıcı olarak senet kuvvetinde değildir.&nbsp;<br />\r\nSenet kesin olarak kullanılan ve kanunun &ouml;zel bir anlam y&uuml;klediği belge t&uuml;r&uuml;d&uuml;r. Oysa, her belge senet olmadığı gibi, kanunun senet dışında da delil olarak kabul ettiği belgeler mevcuttur. &Ouml;rneğin, yazılı delil başlangıcı, niteliği itibarıyla bir belgedir, ancak kanunda kabul edilen şekliyle senet değildir. Bunun dışında g&uuml;n&uuml;m&uuml;zde elektronik ortamda oluşturulan veriler mevcuttur. Elektronik ortamda oluşturulan bu veriler genel anlamda belgedir, ancak senet sayılmamaktadır.&nbsp;<br />\r\n<br />\r\nGerek 1086 sayılı Kanunda gerekse Tasarıda d&uuml;zenlenen h&uuml;k&uuml;mler arasında elektronik ortamda oluşturulan verilerin tamamı değil bir kısmı senet h&uuml;km&uuml;nde kabul edilmiştir. Dikkat edilirse, bu t&uuml;r elektronik belgeler dahi teknik anlamda senet değil, senet h&uuml;km&uuml;ndedir.<br />\r\nAyrıca, belgenin &uuml;st kavram olarak kabul edilmiş olması, ilerde meydana gelecek teknik gelişmelere daha kolay ayak uydurulmasını ve bu y&ouml;nde d&uuml;zenleme yapılmasını m&uuml;mk&uuml;n kılacaktır.<br />\r\n<br />\r\nHerhangi bir veri taşıyıcısı, bug&uuml;n anladığımız anlamda senet ya da g&uuml;venli elektronik imza ile imzalanmış elektronik verilerden daha kuvvetli bir ikna g&uuml;c&uuml;ne ve g&uuml;venliğe sahipse, onun kesin delil olarak kabul&uuml; de kolaylaşacak, bunun i&ccedil;in kanunun sisteminin bozulması da gerekmeyecektir.<br />\r\n<br />\r\nA&ccedil;ıklanan sebeplerle, belge ve senet arasında bir ayrım yapılarak, senetle ispat h&uuml;k&uuml;mleri g&ouml;zden ge&ccedil;irilmiş ve yeniden d&uuml;zenlenmiştir. 1086 sayılı Kanunda yer alan bazı h&uuml;k&uuml;mler bu B&ouml;l&uuml;mden &ccedil;ıkarılmış bulunmaktadır. Bunun bir sebebi, delillere ilişkin genel b&ouml;l&uuml;mde bu h&uuml;k&uuml;mlerin yerini alabilecek h&uuml;k&uuml;mlere yer verilmesi, diğer sebebi ise Tasarıda d&uuml;zenlenen maddelerden bir kısmının kaldırılan h&uuml;k&uuml;mleri de kapsayıcı nitelikte olmasıdır. 1086 sayılı Kanunda yer alan bazı h&uuml;k&uuml;mler ise g&uuml;ncelliğini kaybettiğinden kaldırılmış bulunmaktadır.<br />\r\n<br />\r\n1086 sayılı Kanunda yer alan bazı h&uuml;k&uuml;mler D&ouml;rd&uuml;nc&uuml; Kısmın İkinci B&ouml;l&uuml;m&uuml;ne alınmamıştır. &Ccedil;&uuml;nk&uuml;, belge ve senetle ilgili bazı hususlar, dilek&ccedil;elerin verilmesiyle bazıları da ispat ve delillere ilişkin genel h&uuml;k&uuml;mlerle doğrudan bağlantılıdır. &Ouml;rneğin, tarafların ellerindeki belgeleri dilek&ccedil;elerine ekleyerek mahkemeye vermelerine ilişkin hususlar dilek&ccedil;elere ilişkin B&ouml;l&uuml;mde, başka yerden getirilecek delillerle, delillerin incelenmesi genel olarak ispat ve delillere ilişkin genel h&uuml;k&uuml;mler i&ccedil;inde d&uuml;zenlenmiştir. Bunun dışında, uygulanma kabiliyeti kalmayan h&uuml;k&uuml;mlerle, kaldırılması boşluk doğurmayacak h&uuml;k&uuml;mlere bu Tasarıda yer verilmemiştir. &Ouml;rneğin, 1086 sayılı Kanunun 305 inci maddesinde yer alan, n&uuml;fus kayıtlarının zıyaı h&acirc;linde o kimsenin ailesine ilişkin &ouml;zel belge ve kayıtların delil olarak kullanılacağı hakkındaki h&uuml;kme Tasarıda yer verilmemiştir.<br />\r\nZira, bu h&uuml;k&uuml;m mevcut olmasa da zaten genel h&uuml;k&uuml;mlere g&ouml;re ispatı ve tespiti m&uuml;mk&uuml;nd&uuml;r, nitekim Yargıtay uygulaması da bu y&ouml;ndedir. Yeni delil ibrazına ilişkin husus (HUMK m. 306), tahkikata ilişkin B&ouml;l&uuml;mde &ldquo;Yeni delil g&ouml;sterilmesi&rdquo; kenar başlığı ile, delil ibraz eden tarafın karşı tarafın onayı olmadan bundan vazge&ccedil;emeyeceğine ilişkin h&uuml;k&uuml;m ise (HUMK m. 307) delillere ilişkin genel h&uuml;k&uuml;mlerde &ldquo;Delilden vazge&ccedil;me&rdquo; başlığı altında d&uuml;zenlenmiştir. Senetlere ilişkin bazı h&uuml;k&uuml;mler ise 1086 sayılı Kanunda ayrı ayrı maddelerde d&uuml;zenlenmiş olmalarına rağmen aslında aynı hususa ilişkindir; benzer mahiyetteki h&uuml;k&uuml;mler m&uuml;mk&uuml;n olduğunca sadeleştirilerek birleştirilmiştir. Hukuk&icirc; işlemlerin belli bir miktar veya değerin &uuml;zerinde olması h&acirc;linde senetle ispatı kuralı muhafaza edilmiştir. Akdeniz &uuml;lkeleri olan Fransa, İtalya ve Yunanistan hukukunda da senetle ispat kuralı korunmaktadır. Yaklaşık y&uuml;zelli yılın &uuml;zerinde bir uygulaması olan ve uygulamada herhangi bir şik&acirc;yet konusu olmayan senetle ispat kuralı, Komisyonumuz tarafından da &ccedil;oğunlukla kabul edilmiştir.<br />\r\n<br />\r\nD&ouml;rd&uuml;nc&uuml; Kısmın &Uuml;&ccedil;&uuml;nc&uuml; B&ouml;l&uuml;m&uuml;nde yemin d&uuml;zenlenmiştir. Tamamlayıcı yemine Tasarıda yer verilmemiştir. Takdiri delillerle ispatın m&uuml;mk&uuml;n olduğu h&acirc;llerde, h&acirc;kimin kanaatini yemin ile değil, diğer delillerle tamamlaması kabul edilmiştir. 1086 sayılı Kanunda yer alan yemin form&uuml;l&uuml;, taraf yemini d&uuml;zenlemesinde, daha laik hale getirilmeye &ccedil;alışılmıştır.<br />\r\n<br />\r\nD&ouml;rd&uuml;nc&uuml; Kısmın D&ouml;rd&uuml;nc&uuml; B&ouml;l&uuml;m&uuml;nde d&uuml;zenlenen tanıkla ilgili h&uuml;k&uuml;mlerde, &ouml;nemli bir değişiklik yapılmamıştır.<br />\r\n<br />\r\nD&ouml;rd&uuml;nc&uuml; Kısmın Beşinci B&ouml;l&uuml;m&uuml;nde bilirkişi incelemesi d&uuml;zenlenmiştir. Bu B&ouml;l&uuml;mde yeni olarak, bilirkişinin sır saklama y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml; ve bilirkişinin yetkilerine yer verilmiştir. Ceza Muhakemesi Kanununun bilirkişi ile ilgili h&uuml;k&uuml;mlerine paralel bir d&uuml;zenleme yapılarak, bilirkişilerin b&ouml;lge adliye mahkemesi adliyargı adalet komisyonları tarafından her yıl d&uuml;zenlenecek listelerde yer alan kişiler arasından se&ccedil;ilmesi kabul edilmiştir. B&ouml;ylelikle bilirkişilerin uzman kişilerden oluşması, hukuk&icirc; konularda gereksiz yere bilirkişiye başvurulmaması ve denetimi sağlanmak istenmiştir.<br />\r\nBilirkişi, T&uuml;rk Ceza Kanunu anlamında kamu g&ouml;revlisi olarak kabul edilmiştir. Bilirkişinin kasten veya ağır kusuru sonucu d&uuml;zenlediği ger&ccedil;eğe aykırı raporun karara esas alınması nedeniyle zarar g&ouml;ren kişilerin devlete karşı sorumluluk davası a&ccedil;abileceği d&uuml;zenlenmiştir.<br />\r\n<br />\r\nD&ouml;rd&uuml;nc&uuml; Kısmın Altıncı B&ouml;l&uuml;m&uuml;nde keşifle ilgili d&uuml;zenlemeye yer verilmiştir. 1086 sayılı Kanundan farklı şekilde, keşfin sadece taşınmazlara ilişkin olmadığı daha a&ccedil;ık olarak d&uuml;zenlenmiş, ayrıca keşfe katlanma zorunluluğu ve soybağının tespiti i&ccedil;in inceleme yapılması hakkında yeni h&uuml;k&uuml;mler getirilmiştir.<br />\r\n<br />\r\nAltıncı Kısmında basit yargılama usul&uuml; d&uuml;zenlenmiştir. Seri yargılama ve s&ouml;zl&uuml; yargılama usullerine Taslakta yer verilmemiştir. 1086 sayılı Kanunda seri yargılama usul&uuml;, sadece cevap dilek&ccedil;esi ile cevaba cevap ve ikinci cevap dilek&ccedil;esi i&ccedil;in yazılı yargılama usul&uuml;ne nazaran kısa s&uuml;reler &ouml;ng&ouml;rmekteydi. Bu kısa s&uuml;reler dışında yazılı yargılama usul&uuml;ne atıf yapıldığı i&ccedil;in, seri yargılama usul&uuml; ile yazılı yargılama usul&uuml; arasında bir fark bulunmamakta idi. Hatta cevap ve diğer dilek&ccedil;eler i&ccedil;in &ccedil;oğu kez mahkemeye m&uuml;racaat ederek s&uuml;renin uzatılması istendiğinden ve bu talep kabul edildiğinden, adı seri olan yargılama usul&uuml; &ccedil;oğu kez yazılı yargılama usul&uuml;nden daha uzun s&uuml;rmekte idi. Bu nedenle uygulamada ismi dışında seri bir yargılama &ouml;ng&ouml;rmeyen bu usule yer verilmemiştir. Keza s&ouml;zl&uuml; yargılama usul&uuml; de uygulamada yazılı yargılama usul&uuml; gibi uygulandığından, bu iki yargılama usul&uuml; kaldırılarak, basit yargılama usul&uuml;, yazılı yargılama usul&uuml;ne nazaran daha hızlı daha kolay bir usul h&acirc;line gerilmeye &ccedil;alışılmıştır.<br />\r\n<br />\r\nBasit yargılama us&ucirc;l&uuml;nde yapılan d&uuml;zenlemede, genel hatlarıyla, sadece dava ve cevap dilek&ccedil;esi şeklinde, dilek&ccedil;e aşamasına yer verilmiş, cevaba cevap ve ikinci cevap dilek&ccedil;esi yani replik d&uuml;plik safhası &ccedil;ıkartılmıştır. &Ouml;n inceleme ve tahkikat aşamaları birleştirilmiş, ilk duruşmada &ouml;n incelemeye ilişkin işlemlerin de yapılmasına imk&acirc;n tanınmıştır. Kural olarak ilk duruşmadan sonra, en fazla iki duruşmada (yani en fazla &uuml;&ccedil; duruşma) tahkikatın tamamlanması, &ccedil;ok &ouml;zel durumlarda duruşma sayısının artırılması ama&ccedil;lanmıştır. S&ouml;zl&uuml; yargılama ve h&uuml;km&uuml;n verilmesinin aynı duruşmada yapılması ve h&uuml;km&uuml;n de kural olarak aynı duruşmada t&uuml;m unsurlarıyla tefhimi d&uuml;ş&uuml;n&uuml;lm&uuml;şt&uuml;r.<br />\r\n<br />\r\nYargılamanın daha kısa s&uuml;rede sonu&ccedil;landırılabilmesi i&ccedil;in, davanın takipsiz bırakılması h&acirc;linde bir defa dosya işlemden kaldırıldıktan sonra, davanın a&ccedil;ılmamış sayılmasına karar verilmesi kabul edilmiş, iddia ve savunmanın değiştirilip genişletilmesinin başladığı an &ouml;ne &ccedil;ekilmiş, duruşmalar arasındaki s&uuml;reler kısa tutulmuş, tahkikat işlemlerinin kısa s&uuml;rede tamamlanması &ouml;ng&ouml;r&uuml;lm&uuml;şt&uuml;r.<br />\r\n<br />\r\nKanun yolları Sekizinci Kısımda d&uuml;zenlenmiş, 1086 sayılı Kanuna 5236 sayılı Kanunla eklenen istinaf ve temyiz ile ilgili h&uuml;k&uuml;mler aynen muhafaza edilmiştir. Bu h&uuml;k&uuml;mler 1 Haziran 2005 tarihinde y&uuml;r&uuml;rl&uuml;ğe girdiğinden ve fakat b&ouml;lge adliye mahkemelerinin g&ouml;reve başlamaması nedeniyle hen&uuml;z uygulanmadığından ve bu h&uuml;k&uuml;mlerin değiştirilmesi y&ouml;n&uuml;nde bir eleştiri yapılmadığından aynen benimsenmiştir.<br />\r\n<br />\r\nGe&ccedil;ici hukuk&icirc; koruma kavramı doktrinde kullanılmakla birlikte, Tasarıda ilk defa yer almaktadır. Ge&ccedil;ici hukuk&icirc; korumalar Onuncu Kısımda d&uuml;zenlenmiştir. Asıl niha&icirc; hukuk&icirc; korumanın zaman alması ve ayrıntılı bir incelemeyi gerekli kılması sebebiyle, g&uuml;n&uuml;m&uuml;zde ge&ccedil;ici hukuk&icirc; korumalar daha da &ouml;nem kazanmıştır. Hatta denilebilir ki, asıl yargılama veya davadan &ouml;nce, ge&ccedil;ici korumalar adeta bir &ouml;n yargılama h&acirc;lini almıştır. Ge&ccedil;ici hukuk&icirc; korumalar, hak arama &ouml;zg&uuml;rl&uuml;ğ&uuml; ve kişilerin haklarının korunması bakımından &ouml;zel bir &ouml;neme sahiptir. Hak arama &ouml;zg&uuml;rl&uuml;ğ&uuml; &ccedil;er&ccedil;evesinde, hukuk&icirc; korunma talebi i&ccedil;in artık g&uuml;n&uuml;m&uuml;zde, &ldquo;etkin hukuk&icirc; koruma&rdquo; ihtiyacı ve talebinden s&ouml;z edilmektedir. Yani, bir devletin sadece yalın olarak hak arama ve hukuk&icirc; korunma yollarını d&uuml;zenlemesi ve bunları a&ccedil;ık tutması yeterli değildir; aynı zamanda bu yolların etkinliğini sağlamak, elde edilecek kararların uygulanabilir olması imk&acirc;nını ayakta tutmak, hukuk&icirc; korunma ihtiyacı ile orantılı ve bağlantılı bir y&ouml;ntemi benimsemek gibi y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml; s&ouml;z konusudur.<br />\r\n<br />\r\nBelirtilen sebeplerle g&uuml;n&uuml;m&uuml;zde, klasik ve temel ge&ccedil;ici hukuk&icirc; koruma t&uuml;r&uuml; olan ihtiyat&icirc; tedbir dışında, bir &ccedil;ok yeni ge&ccedil;ici hukuk&icirc; koruma t&uuml;r&uuml; benimsenmiştir. Asıl dava veya yargılama konusuna ilişkin olarak ihtiyat&icirc; tedbirler yanında, para alacaklarına ilişkin takibin sonucunun g&uuml;vence altına alınabilmesi i&ccedil;in ihtiyat&icirc; haciz, delillerin korunması i&ccedil;in delil tespiti bilinen ge&ccedil;ici hukuk&icirc; koruma t&uuml;rleridir. Ancak, &ouml;zel kanunlarda bu ge&ccedil;ici hukuk&icirc; koruma t&uuml;rlerinin i&ccedil;inde yer almayan farklı ge&ccedil;ici hukuk&icirc; korumalara da yer verilmiştir. &Ouml;rneğin, aile hukukuna ilişkin bir &ccedil;ok ge&ccedil;ici hukuk&icirc; koruma, &ouml;nleyici tedbir, koruma &ouml;nlemleri ve aile ilişkilerinin ge&ccedil;ici d&uuml;zenlenmesi gibi farklı ge&ccedil;ici hukuk&icirc; korumalar mevcuttur. Bu ge&ccedil;ici korumalar, bazı y&ouml;nleri ile ihtiyat&icirc; tedbire benzemekle birlikte, ihtiyat&icirc; tedbirden daha farklı ve geniş bir alana sahiptir. Bu sebeple, genel bir ge&ccedil;ici hukuk&icirc; koruma başlığı altında temel olarak ihtiyat&icirc; tedbir d&uuml;zenlenmekle birlikte, diğer ge&ccedil;ici hukuk&icirc; korumalara da değinilmiştir.<br />\r\n<br />\r\nİhtiyat&icirc; haciz, İcra ve İfl&acirc;s Kanununda d&uuml;zenlendiğinden, bu başlık altında d&uuml;zenlenmemiş, bu Kısımda ihtiyati tedbir ve delil tespiti ayrıntılı olarak d&uuml;zenlenip, diğer &ouml;zel ge&ccedil;ici hukuk&icirc; korumalara ilişkin genel atıf yapılmakla yetinilmiştir. Bunun dışında, klasik ihtiyat&icirc; tedbir t&uuml;r&uuml; olan teminat ama&ccedil;lı ihtiyat&icirc; tedbirler dışında, eda veya d&uuml;zenleme ama&ccedil;lı ihtiyat&icirc; tedbirler de olduk&ccedil;a yaygınlaşmıştır.<br />\r\n<br />\r\nHukuk yargılaması bakımından en sorunlu alanlardan biri de ge&ccedil;ici hukuk&icirc; korumalardır. Uygulamada, haksız alınan ihtiyat&icirc; tedbir veya ihtiyat&icirc; haciz kararlarından dolayı telafisi g&uuml;&ccedil; zararların ortaya &ccedil;ıkmasına rastlandığı gibi, gerekli durumlarda ilgili ge&ccedil;ici hukuk&icirc; korumaya karar verilmediğinden dolayı elde edilen h&uuml;km&uuml;n bazen uygulama kıymeti kalmamaktadır. Ayrıca, uygulamamızda &ouml;nemli bir sorun da ge&ccedil;ici hukuk&icirc; korumaların birbirine karıştırılması ve gereği gibi uygulanmamasıdır. &Ouml;rneğin, para alacakları i&ccedil;in kural olarak ihtiyat&icirc; haciz istenmesi gerekirken ihtiyat&icirc; tedbir talep edilmesi, delil tespiti yerine bir dava t&uuml;r&uuml; olan tespit davası a&ccedil;ılması, bunun en sık rastlanan &ouml;rnekleridir. Ayrıca, bir mahkemeden ihtiyat&icirc; tedbir talep edilip reddedilmesi durumunda, başka bir mahkemeden b&ouml;yle bir talepte bulunulması gibi davranışlara da uygulamada sık&ccedil;a rastlanılmaktadır.<br />\r\n<br />\r\nYukarıda a&ccedil;ıklanan hususlar g&ouml;z&ouml;n&uuml;nde tutularak ge&ccedil;ici hukuk&icirc; korumalar, bu &ccedil;er&ccedil;evede ihtiyat&icirc; tedbir ve delil tespiti yeniden d&uuml;zenlenmiştir.<br />\r\n<br />\r\nTahkim, &uuml;lkemizde ilk kabul edildiği tarih olan 1856 yılından bu yana etkin olarak kullanılamamaktadır. Tahkim kurumunu etkin kılmak i&ccedil;in, yani &ouml;z&uuml; var olan tahkim k&uuml;lt&uuml;r&uuml;n&uuml; &uuml;lkemizde geliştirmek amacıyla yapılması gereken ilk iş ş&uuml;phesiz i&ccedil; tahkim yani mill&icirc; tahkime ilişkin usul ve esasları d&uuml;zenleyen h&uuml;k&uuml;mleri, g&uuml;n&uuml;m&uuml;z tahkim yaklaşımına uygun h&acirc;le getirmektir.<br />\r\nTasarının Onbirinci Kısmında yer alan tahkim h&uuml;k&uuml;mleri d&uuml;zenlenirken, 4686 sayılı Milletlerarası Tahkim Kanunu esas alınmıştır. 4686 sayılı Kanun, bir ka&ccedil; istisna dışında esas olarak 1985 tarihli Uncitral Model Kanununu benimsemiş ancak iktibas esnasında bazı konular eksik alınmış ve yine bazı konularda tercihler Model Kanundan değişik olarak kullanılmıştır.<br />\r\n<br />\r\nTahkim hakkındaki h&uuml;k&uuml;mler, Milletlerarası Tahkim Kanunundaki h&uuml;k&uuml;mlerin hemen hemen aynısıdır. Tasarıda, 4686 sayılı Kanundaki h&uuml;k&uuml;mlerin kabul&uuml; ile mill&icirc; ve milletlerarası tahkim h&uuml;k&uuml;mleri arasındaki fark &ouml;nlenmek istenmiştir. Ayrıca d&uuml;nyada pek &ccedil;ok &uuml;lkenin milletlerarası ve mill&icirc; tahkim i&ccedil;in esas aldığı Uncitral Tahkim kuralları i&ccedil; tahkim bakımından da benimsenmiştir.<br />\r\n<br />\r\n1086 sayılı kanunda &ouml;ng&ouml;r&uuml;len cezalar, Tasarıda &ldquo;disiplin hapsi&rdquo; ve &ldquo;disiplin para cezası&rdquo; olarak d&uuml;zenlenmiştir. Disiplin hapsi, &ouml;zellikle duruşmanın d&uuml;zeninin sağlanması gibi h&acirc;llerde &ouml;ng&ouml;r&uuml;lm&uuml;şt&uuml;r. Davanın g&ouml;r&uuml;lmesi sırasında k&ouml;t&uuml; niyetli davranışları &ouml;nlemek ve yargılamayı daha etkin kılabilmek i&ccedil;in ise disiplin para cezası d&uuml;zenlenmiştir. Bu cezaların tecili veya ertelenmesi gibi sebeplerle uygulanamaması &ouml;nlenerek, yargılamanın işleyişinin g&uuml;vence altına alınması ama&ccedil;lanmıştır.&nbsp;<br />\r\nAyrıca bu Tasarıda, disiplin para cezasının &ouml;zelliklerinin neler olduğu ve bu cezanın nasıl tahsil edileceği hususları da a&ccedil;ıklığa kavuşturulmuştur.<br />\r\n<br />\r\nTasarı, y&uuml;r&uuml;rl&uuml;k ve y&uuml;r&uuml;tme maddesi ile birlikte 455 maddeden oluşmaktadır. Bilindiği &uuml;zere, 1086 sayılı Kanun 582 maddedir. Genel gerek&ccedil;enin değişik yerlerinde de a&ccedil;ıklandığı &uuml;zere, 1086 sayılı Kanunun farklı kısımlarında yer alan, ancak, birbirine benzer h&uuml;k&uuml;mleri d&uuml;zenleyen maddeler bu Tasarıda tek madde altında birleştirilmiştir. Ayrıca, y&uuml;r&uuml;rl&uuml;kteki Kanunun yaklaşık seksen maddesi değişik zamanlarda y&uuml;r&uuml;rl&uuml;kten kaldırıldığı h&acirc;lde, kaldırılan maddeler dikkate alınarak tesels&uuml;l ettirilmemiştir. Dolayısıyla hazırlanan Tasarıdaki madde sayısı, 1086 sayılı Kanunda yer alan madde sayısından daha azdır. Bunun sebebi, uygulaması olmayan, eskimiş ya da başka kanunlarda d&uuml;zenlenmiş maddelerin Tasarıya alınmamış olmasıdır.</h2>\r\n'),
(5, 'HMK 1', '<p><strong><em>Mahkemeler </em></strong>ancak kanunla kurulurlar ve g&ouml;revleri kanunla d&uuml;zenlenir. Taraflar aralarında anlaşarak bir uyuşmazlığın &ccedil;&ouml;z&uuml;m&uuml;n&uuml;n belirledikleri mahkeme ya da mahkemelerde g&ouml;r&uuml;lmesini &ouml;nceden kararlaştıramazlar. G&ouml;rev kuralları kamu d&uuml;zeni ile ilgili olduğu i&ccedil;in hakim tarafından re&#39;sen dikkate alınır, taraflar da yargılama sona erinceye kadar mahkemenin g&ouml;revsiz olduğunu ileri s&uuml;rebilirler. Bu konuda taraflarca &ouml;nceden yapılmış olan anlaşma ya da anlaşmalar batıldır (baştan itibaren h&uuml;k&uuml;ms&uuml;zd&uuml;r). G&ouml;rev kamu d&uuml;zeni ile ilgili olduğu i&ccedil;in aynı zamanda dava şartıdır. Yani g&ouml;revsiz bir mahkemede a&ccedil;ılan dava; taraflardan birinin talebi ile ya da hakim tarafından farkedilmesi durumunda davaya devam edilemez ve g&ouml;revsizlik kararı verilerek dava reddedilir. &quot;G&ouml;rev&quot; konusu dava şartı olduğu i&ccedil;in mahkemenin uyuşmazlığın esasına (davanın esasına) girmesini engelleyici bir sebeptir.</p>\r\n');
INSERT INTO `bilgitbl` (`id`, `bilgibaslik`, `bilgi`) VALUES
(6, 'HMK 1', '<p>Adalet Bakanlığından:<br />\r\n<br />\r\nHUKUK MUHAKEMELERİ KANUNU Y&Ouml;NETMELİĞİ<br />\r\n<br />\r\nBİRİNCİ KISIM<br />\r\n<br />\r\nGenel H&uuml;k&uuml;mler<br />\r\n<br />\r\nBİRİNCİ B&Ouml;L&Uuml;M<br />\r\n<br />\r\nAma&ccedil;, Kapsam, Dayanak ve Tanımlar<br />\r\n<br />\r\nAma&ccedil;<br />\r\n<br />\r\nMADDE 1 &ndash; (1) Bu Y&ouml;netmeliğin amacı, adli yargı ilk derece hukuk mahkemeleri ve b&ouml;lge adliye mahkemeleri hukuk dairelerinin yargılama ve yazı işleri hizmetlerinin y&uuml;r&uuml;t&uuml;lmesine dair usul ve esasları d&uuml;zenlemektir.<br />\r\n<br />\r\nKapsam<br />\r\n<br />\r\nMADDE 2 &ndash; (1) Bu Y&ouml;netmelik, 12/1/2011 tarihli ve 6100 sayılı Hukuk Muhakemeleri Kanununun uygulama alanını, adli yargı ilk derece hukuk mahkemeleri ve b&ouml;lge adliye mahkemeleri hukuk dairelerinde tutulacak kayıtlar ile yazı işleri hizmetlerinin y&uuml;r&uuml;t&uuml;lmesi ve bu işlemlerde UYAP&rsquo;ın kullanılmasına dair usul ve esasları kapsar.<br />\r\n<br />\r\nDayanak<br />\r\n<br />\r\nMADDE 3 &ndash; (1) Bu Y&ouml;netmelik, Hukuk Muhakemeleri Kanununun 118 inci maddesinin ikinci fıkrası, 317 nci maddesinin d&ouml;rd&uuml;nc&uuml; fıkrası, 386 ncı maddesi, 445 inci maddesinin beşinci fıkrası ve 449 uncu maddesine dayanılarak hazırlanmıştır.<br />\r\n<br />\r\nTanımlar<br />\r\n<br />\r\nMADDE 4 &ndash; (1) Bu Y&ouml;netmelikte ge&ccedil;en;<br />\r\n<br />\r\na) Avukat Bilgi Sistemi: Avukatların internet &uuml;zerinden UYAP&rsquo;a erişim yaparak elektronik ortamda işlem yapabilmelerini sağlayan sistemi,<br />\r\n<br />\r\nb) Bakanlık: Adalet Bakanlığını,<br />\r\n<br />\r\nc) Bilirkişi Bilgi Sistemi: Bilirkişilerin internet &uuml;zerinden UYAP&rsquo;a erişim yaparak elektronik ortamda işlem yapabilmelerini sağlayan sistemi,<br />\r\n<br />\r\n&ccedil;) Bilişim sistemi: Bilgisayar, &ccedil;evre birimleri, iletişim altyapısı ve programlardan oluşan veri işleme, saklama ve iletmeye y&ouml;nelik sistemi,<br />\r\n<br />\r\nd) Daire başkanı: B&ouml;lge adliye mahkemesi hukuk dairesi başkanını,<br />\r\n<br />\r\ne) Dış birim: UYAP sistemine d&acirc;hil olmayan diğer kamu kurumları ve &ouml;zel kuruluşları,<br />\r\n<br />\r\nf) Elektronik ortam: Bilişim sistemi ve bilişim ağından oluşan toplam ortamı,<br />\r\n<br />\r\ng) Elektronik veri: Elektronik, optik veya benzeri yollarla &uuml;retilen, taşınan veya saklanan kayıtları,<br />\r\n<br />\r\nğ) Erişim: Bir bilişim sistemine bağlanarak kullanım imk&acirc;nı kazanılmasını,<br />\r\n<br />\r\nh) Hukuk dairesi: B&ouml;lge adliye mahkemesi hukuk dairesini,<br />\r\n<br />\r\nı) Kanun: Hukuk Muhakemeleri Kanununu,<br />\r\n<br />\r\ni) Kayıt: Fizik&icirc; veya elektronik ortamda &uuml;retilen ya da bu ortama aktarılan veya taşınan, bilgi, belge ve verinin saklanmasını,<br />\r\n<br />\r\nj) Kurum Bilgi Sistemi: T&uuml;zel kişilerin internet &uuml;zerinden UYAP&rsquo;a erişim yaparak elektronik ortamda işlem yapabilmelerini sağlayan sistemi,<br />\r\n<br />\r\nk) &Ouml;n b&uuml;ro: Davanın a&ccedil;ılmasından başlamak &uuml;zere yargılama ile ilgili b&uuml;t&uuml;n işlemler ile mahkemelere sunulan veya mahkemelerden talep edilen her t&uuml;rl&uuml; evraka ilişkin işlemlerin yapıldığı, genellikle adliyelerin giriş kısımlarında bulunan ve t&uuml;m hukuk mahkemelerine hizmet veren yazı işleri birimini,<br />\r\n<br />\r\nl) Ses ve G&ouml;r&uuml;nt&uuml; Bilişim Sistemi (SEGBİS): UYAP Bilişim Sisteminde ses ve g&ouml;r&uuml;nt&uuml;n&uuml;n aynı anda elektronik ortamda iletildiği, kaydedildiği ve saklandığı ses ve g&ouml;r&uuml;nt&uuml; bilişim sistemini,<br />\r\n<br />\r\nm) Tevzi b&uuml;rosu: Dava ve işlerin mahkemelere g&ouml;nderilmesi işleminin yapıldığı yeri,<br />\r\n<br />\r\nn) Tevzi kriteri: Dava, istinabe ve işlerin mahkemelere dağılımının ne şekilde yapılacağını g&ouml;steren usul ve esasları,<br />\r\n<br />\r\no) Ulusal Yargı Ağı Bilişim Sistemi (UYAP): Adalet hizmetlerinin elektronik ortamda y&uuml;r&uuml;t&uuml;lmesi amacıyla oluşturulan yargı bilişim sistemini,<br />\r\n<br />\r\n&ouml;) Vatandaş Bilgi Sistemi: Vatandaşların internet &uuml;zerinden UYAP&rsquo;a erişim yaparak elektronik ortamda işlem yapmalarını sağlayan sistemi,<br />\r\n<br />\r\np) Veri: Bilgisayar tarafından &uuml;zerinde işlem yapılabilen her t&uuml;rl&uuml; değeri,<br />\r\n<br />\r\nifade eder.<br />\r\n<br />\r\nİKİNCİ B&Ouml;L&Uuml;M<br />\r\n<br />\r\nYazı İşleri Hizmetlerinin Y&uuml;r&uuml;t&uuml;lmesine Dair Genel Esaslar<br />\r\n<br />\r\nUYAP&rsquo;ın kullanılması<br />\r\n<br />\r\nMADDE 5 &ndash; (1) Mahkemeler ve hukuk dairelerinin iş s&uuml;re&ccedil;lerindeki her t&uuml;rl&uuml; veri, bilgi ve belge akışı ile dok&uuml;mantasyon işlemleri, bu işlemlere ilişkin her t&uuml;rl&uuml; kayıt, dosyalama, saklama ve arşivleme işlemleri ile uyum ve işbirliği sağlanmış dış birimlerle yapılacak her t&uuml;rl&uuml; işlemler UYAP ortamında ger&ccedil;ekleştirilir.<br />\r\n<br />\r\n(2) Daire başkanı, h&acirc;kim, &uuml;ye, yazı işleri m&uuml;d&uuml;r&uuml; ve diğer personel iş listesini g&uuml;nl&uuml;k olarak kontrol etmek ve yargılamanın bulunduğu aşamanın gereklerini yerine getirmekle y&uuml;k&uuml;ml&uuml;d&uuml;r.<br />\r\n<br />\r\n(3) Mahkemelere ve hukuk dairelerine fizik&icirc; olarak verilen ve g&ouml;nderilen her t&uuml;rl&uuml; evrak, elektronik ortama aktarılarak UYAP&rsquo;a kaydedilir ve ilgili birime g&ouml;nderilir.<br />\r\n<br />\r\n(4) Taraf ve vekilleri ile diğer ilgililer g&uuml;venli elektronik imza ile imzalamak suretiyle UYAP vasıtasıyla mahkemeler veya hukuk dairelerine elektronik ortamda bilgi ve belge g&ouml;nderebilirler.<br />\r\n<br />\r\n(5) Gelen evraktan sorumlu personel, UYAP &uuml;zerinden mahkeme veya hukuk dairelerine g&ouml;nderilen ve iş listesine d&uuml;şen belgeleri derhal ilgili kişiye ya da doğrudan dosyasına aktarır. H&acirc;kimin onayını gerektiren evrak h&acirc;kimin iş listesine y&ouml;nlendirilir.<br />\r\n<br />\r\n(6) Tutanak, belge ve kararlar elektronik ortamda d&uuml;zenlenir ve gerekli olanlar ilgilileri tarafından g&uuml;venli elektronik imza ile imzalanır. Elektronik ortamda d&uuml;zenlenen ve g&uuml;venli elektronik imza ile imzalanan evrak UYAP kapsamındaki birimlere elektronik ortamda g&ouml;nderilir. Ayrıca fizik&icirc; olarak g&ouml;nderilmez.<br />\r\n<br />\r\n(7) Teknik nedenlerle fizik&icirc; olarak d&uuml;zenlenen belge veya kararlar, engelin ortadan kalkmasından sonra derhal elektronik ortama aktarılır, yetkili kişilerce g&uuml;venli elektronik imza ile imzalanarak UYAP&rsquo;a kaydedilir ve gerektiğinde UYAP vasıtasıyla ilgili birimlere iletilir. Bu şekilde elektronik ortama aktarılarak ilgili birimlere iletilen belge ve kararların asılları mahallinde saklanır, ayrıca fizik&icirc; olarak g&ouml;nderilmez. Ancak, belge veya kararın aslının incelenmesinin zorunlu olduğu h&acirc;ller saklıdır. Elektronik ortama aktarılması imk&acirc;nsız olan belgeler ise fiziki ortamda saklanır ve gerektiğinde fizik&icirc; olarak g&ouml;nderilir.<br />\r\n<br />\r\n(8) UYAP &uuml;zerinden hazırlanmış ve g&uuml;venli elektronik imza ile imzalanmış evrakın dış birimlere elektronik ortamda g&ouml;nderilememesi halinde; belge veya kararın fizik&icirc; &ouml;rneği alınır, g&uuml;venli elektronik imza ile imzalanmış aslının aynı olduğu belirtilerek altı h&acirc;kim veya g&ouml;revlendirdiği yazı işleri m&uuml;d&uuml;r&uuml; tarafından imzalanmak sureti ile g&ouml;nderilir.<br />\r\n<br />\r\n(9) Elektronik ortamda yapılan işlemlerde s&uuml;re g&uuml;n sonunda biter. Elektronik ortamda yapılacak işlemlerin, ertesi g&uuml;ne sarkmaması a&ccedil;ısından saat 00:00&rsquo;a kadar yapılması zorunludur.<br />\r\n<br />\r\n(10) Fizik&icirc; ortamda yapılan işlemlerde s&uuml;re mesai saati sonunda biter.<br />\r\n<br />\r\nYazı işleri hizmetlerinin y&uuml;r&uuml;t&uuml;lmesi<br />\r\n<br />\r\nMADDE 6 &ndash; (1) Mahkemelerde ve hukuk dairelerinde bir yazı işleri m&uuml;d&uuml;r&uuml;, yeterli sayıda zabıt k&acirc;tibi, memur, m&uuml;başir, hizmetli ve diğer g&ouml;revliler bulunur.<br />\r\n<br />\r\n(2) Yazı işleri hizmetleri ilgisine g&ouml;re mahkeme h&acirc;kimi veya daire başkanının denetimi altında, ilgili yazı işleri m&uuml;d&uuml;r&uuml; ve onun y&ouml;netiminde zabıt k&acirc;tibi, memur, m&uuml;başir, hizmetli ve diğer g&ouml;revliler tarafından y&uuml;r&uuml;t&uuml;l&uuml;r.<br />\r\n<br />\r\n(3)Yazı işleri m&uuml;d&uuml;r&uuml;n&uuml;n g&ouml;revleri şunlardır:<br />\r\n<br />\r\na) Mahkeme yazı işlerini denetlemek.<br />\r\n<br />\r\nb) Talep halinde gerek&ccedil;eli kararın tebliğini sağlamak.<br />\r\n<br />\r\nc) Har&ccedil; tahsil m&uuml;zekkerelerini yazmak ve kesinleştirme işlemlerini yapmak.<br />\r\n<br />\r\n&ccedil;) Zabıt k&acirc;tipleri arasında iş b&ouml;l&uuml;m&uuml; yapmak.<br />\r\n<br />\r\nd) Dava dilek&ccedil;esini ve havalesi gereken evrakı havale etmek.<br />\r\n<br />\r\ne) Y&ouml;netimi altında bulunan zabıt k&acirc;tipleri ve diğer memurları yetiştirmek.<br />\r\n<br />\r\nf) Hukuk&icirc; başvuru veya kanun yolları incelemesi i&ccedil;in dosyayla ilgili gerekli işlemleri yapmak ya da yaptırmak.<br />\r\n<br />\r\ng) Bilirkişilere fiziki ortamda teslimi gereken dosyalarla ilgili işlemleri yerine getirmek.<br />\r\n<br />\r\nh) Harcın hesaplanması ve hukuk mahkemeleri veznesi bulunmayan yerlerde tahsiline ilişkin işlemleri yapmak.<br />\r\n<br />\r\nı) Dosyaya ait kıymetli evrak ve değerli eşyanın uygun yerde muhafazasını sağlamak.<br />\r\n<br />\r\ni) Yargılamanın bulunduğu aşamanın gereklerini yerine getirmek.<br />\r\n<br />\r\nj) Arşivin d&uuml;zenli tutulmasını sağlamak.<br />\r\n<br />\r\nk) Teminatın iadesi gereken hallerde gerekli işlemleri yerine getirmek.<br />\r\n<br />\r\nl) Mevzuattan kaynaklanan veya h&acirc;kim tarafından verilen diğer g&ouml;revleri yerine getirmek.<br />\r\n<br />\r\n(4) Zabıt k&acirc;tibinin g&ouml;revleri şunlardır:<br />\r\n<br />\r\na) Duruşma ve keşiflere katılmak, h&acirc;kim tarafından yazdırılanlar ile doğrudan yazılmasına izin verilen beyanları tutanağa yazmak.<br />\r\n<br />\r\nb) Ara kararları yerine getirmek.<br />\r\n<br />\r\nc) Gerek&ccedil;eli kararları h&acirc;kimin bildirdiği şekilde yazmak.<br />\r\n<br />\r\n&ccedil;) Yargılaması devam eden dosyaları d&uuml;zenli ve eksiksiz bir şekilde muhafaza etmek.<br />\r\n<br />\r\nd) Tamamlanıp kesinleşen dosyaları arşive kaldırtmak.<br />\r\n<br />\r\ne) Tarafların dosyaları incelemesine nezaret etmek ve dosya inceleme tutanağını d&uuml;zenlemek.<br />\r\n<br />\r\nf) Karar ve tutanakları dikkat ve itina ile yazarak imzasız bırakmamak.<br />\r\n<br />\r\ng) Cevabı gelmeyen m&uuml;zekkerelerin tekidini yapmak.<br />\r\n<br />\r\nğ) Dizi pusulası d&uuml;zenlemek.<br />\r\n<br />\r\nh) Mevzuattan kaynaklanan veya h&acirc;kim ya da yazı işleri m&uuml;d&uuml;r&uuml;n&uuml;n vereceği diğer g&ouml;revleri yerine getirmek.<br />\r\n<br />\r\n(5) M&uuml;başirin g&ouml;revleri şunlardır:<br />\r\n<br />\r\na) K&acirc;tip tarafından hazırlanan ve duruşması yapılacak dosyaları incelenmek &uuml;zere duruşma g&uuml;n&uuml;nden &ouml;nce h&acirc;kime g&ouml;t&uuml;rmek.<br />\r\n<br />\r\nb) G&uuml;nl&uuml;k duruşma listesini yapmak ve g&ouml;r&uuml;lebilecek bir yere asmak.<br />\r\n<br />\r\nc) Duruşma sırası gelenleri duruşma salonuna davet etmek.<br />\r\n<br />\r\n&ccedil;) Duruşmaya alınanların salondaki yerlerini g&ouml;stermek ve buna uyulmasını sağlamak.<br />\r\n<br />\r\nd) Yemin verilmesi ve kararın a&ccedil;ıklanması başta olmak &uuml;zere, duruşma ve keşif esnasında izlenmesi gereken davranış kurallarını taraflara ve ilgililere a&ccedil;ıklamak.<br />\r\n<br />\r\ne) M&uuml;zekkereler ve tebligatların ilgili kurum ya da kişilere ulaşmasını sağlamak &uuml;zere posta ve zimmet işlemlerini yerine getirmek.<br />\r\n<br />\r\nf) Duruşmanın gizli yapılması kararı alındığında salonu boşaltmak.<br />\r\n<br />\r\ng) H&acirc;kimin uygun bulduğu kişilerin dosyadan fotokopi almasına yardımcı olmak.<br />\r\n<br />\r\nğ) Ertelenen duruşma tarihini yazarak taraflara vermek.<br />\r\n<br />\r\nh) Arşive gitmesi gereken dosyalar ile arşivden &ccedil;ıkarılması gereken dosyalara ilişkin işlemleri yapmak ve arşivi d&uuml;zenli tutmak.<br />\r\n<br />\r\nı) Fiziki ortamda sunulan evrakı en kısa s&uuml;rede dosyasına d&uuml;zenli bir şekilde takmak.<br />\r\n<br />\r\ni) H&acirc;kimin ve yazı işleri m&uuml;d&uuml;r&uuml;n&uuml;n vereceği diğer g&ouml;revleri yerine getirmek.<br />\r\n<br />\r\n(6) Hizmetlinin g&ouml;revleri şunlardır:<br />\r\n<br />\r\na) Mahkemeye ait alanları tertipli, d&uuml;zenli ve temiz halde tutmak.<br />\r\n<br />\r\nb) M&uuml;başirin olmadığı ya da yetersiz kaldığı zamanlarda ona ait işleri yapmak ve yardımcı olmak.<br />\r\n<br />\r\nc) H&acirc;kimin ve yazı işleri m&uuml;d&uuml;r&uuml;n&uuml;n vereceği diğer g&ouml;revleri yerine getirmek.<br />\r\n<br />\r\nİş b&ouml;l&uuml;m&uuml; ve denetim<br />\r\n<br />\r\nMADDE 7 &ndash; (1) Yazı işleri m&uuml;d&uuml;r&uuml;, ilgisine g&ouml;re h&acirc;kimin veya daire başkanının onayını alarak, zabıt k&acirc;tibi, memur, m&uuml;başir ve diğer g&ouml;revliler arasında iş b&ouml;l&uuml;m&uuml; yapar. Yazı işleri b&uuml;rosunun birden fazla h&acirc;kime bağlı &ccedil;alıştığı ilk derece hukuk mahkemelerinde t&uuml;m h&acirc;kimlerin onayı alınır.<br />\r\n<br />\r\n(2) Yazı işleri m&uuml;d&uuml;r&uuml;, hizmetlerin verimli ve d&uuml;zenli bir şekilde y&uuml;r&uuml;t&uuml;lmesi i&ccedil;in zabıt k&acirc;tipleriyle, memur ve diğer g&ouml;revlilere işlerin &ouml;ğretilmesi de d&acirc;hil olmak &uuml;zere her t&uuml;rl&uuml; tedbiri alır. İlgili yazı işleri m&uuml;d&uuml;r&uuml; ve personel işin birikmesi veya gecikmesinden birlikte sorumludur.<br />\r\n<br />\r\n(3) Yazı işleri hizmetlerinin y&uuml;r&uuml;t&uuml;lmesi ve denetlenmesinde gerektiğinde hukuk dairesi başkanı &uuml;yelere g&ouml;rev verebilir.<br />\r\n<br />\r\nH&acirc;kim ve savcı adayları ile avukat stajyerlerinin yazı işleri hizmetlerinde &ccedil;alıştırılması<br />\r\n<br />\r\nMADDE 8 &ndash; (1) Avukat stajyerleri ilgisine g&ouml;re h&acirc;kim veya daire başkanının uygun g&ouml;rmesi ile uygulama bilgilerini geliştirmek amacıyla yazı işleri hizmetlerinde &ccedil;alıştırılabilir. Ancak bir defa yapmakla &ouml;ğrenebilecek işler devamlı yaptırılamaz.<br />\r\n<br />\r\n(2) H&acirc;kim ve savcı adayları, 1/6/2004 tarihli ve 25479 sayılı Resm&icirc; Gazete&rsquo;de yayımlanan Adli Yargı H&acirc;kim ve Savcı Adayları ile İdari Yargı H&acirc;kim Adaylarının Staj D&ouml;nemi ile Staj Mahkemelerine İlişkin Y&ouml;netmelik h&uuml;k&uuml;mlerine g&ouml;re yazı işleri hizmetlerinde &ccedil;alıştırılabilir.<br />\r\n<br />\r\nİKİNCİ KISIM<br />\r\n<br />\r\nMahkemeler ve Hukuk Daireleri Yazı İşleri Hizmetleri<br />\r\n<br />\r\nBİRİNCİ B&Ouml;L&Uuml;M<br />\r\n<br />\r\nKayıtlar, Tahsis Amacı ve Kullanılış Şekli<br />\r\n<br />\r\nTutulacak kayıtlar<br />\r\n<br />\r\nMADDE 9 &ndash; (1) Mahkemeler ve hukuk dairelerinde aşağıda g&ouml;sterilen kayıtların UYAP&rsquo;ta tutulması zorunludur. Bu kayıtlar tarih, sıra numarası, hazırlayan veya onaylayan kişiye g&ouml;re sorgulanabilir şekilde tutulur. Tutulacak kayıtlar şunlardır:<br />\r\n<br />\r\na) Esas kaydı.<br />\r\n<br />\r\nb) Karar kaydı.<br />\r\n<br />\r\nc) İstinaf kaydı.<br />\r\n<br />\r\n&ccedil;) Temyiz kaydı.<br />\r\n<br />\r\nd) İstinabe kaydı.<br />\r\n<br />\r\ne) Değişik işler kaydı.<br />\r\n<br />\r\nf) Duruşma g&uuml;nleri kaydı.<br />\r\n<br />\r\ng) Keşif g&uuml;nleri kaydı.<br />\r\n<br />\r\nğ) Taşra istinaf veya temyiz kaydı.<br />\r\n<br />\r\nh) Satış talep edilen ortaklığın giderilmesi dosyalarının kaydına mahsus esas kaydı.<br />\r\n<br />\r\nı) Ortaklığın giderilmesi satış paralarının kaydına mahsus kasa kaydı.<br />\r\n<br />\r\ni) Tereke esas kaydı.<br />\r\n<br />\r\nj) Tereke karar kaydı.<br />\r\n<br />\r\nk) Tereke kasa kaydı.<br />\r\n<br />\r\nl) Mirası ret kaydı.<br />\r\n<br />\r\nm) Posta mutemet kaydı.<br />\r\n<br />\r\nn) Tevzi kaydı.<br />\r\n<br />\r\no) Zimmet kaydı.<br />\r\n<br />\r\n&ouml;) Har&ccedil; tahsil m&uuml;zekkeresi zimmet kaydı.<br />\r\n<br />\r\np) Vakıf tescil kaydı.<br />\r\n<br />\r\nr) Vesayet ad kaydı.<br />\r\n<br />\r\ns) Kıymetli evrak ve eşya kaydı.<br />\r\n<br />\r\nş) Sosyal inceleme-g&ouml;r&uuml;şme kaydı.<br />\r\n<br />\r\nt) Muhabere kaydı.<br />\r\n<br />\r\nu) Hakem kararları saklama kaydı.<br />\r\n<br />\r\nEsas kaydı<br />\r\n<br />\r\nMADDE 10 &ndash; (1) Davaların aşamalarının işlendiği kayıttır.<br />\r\n<br />\r\n(2) Esas kaydı; sıra numarası, tarafların, varsa kanun&icirc; temsilcileri ile vekillerinin ad ve soyadları, T.C. kimlik numarası, varsa ticaret sicil numarası veya merkezi t&uuml;zel kişi kimlik numarası, davanın konusu, davanın aşaması, nihai karar ve tarihi, kanun yolları işlemleri ve d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\n(3) Davanın aşaması s&uuml;tununa davanın dilek&ccedil;eler, &ouml;n inceleme ve tahkikat aşamalarına ilişkin bilgiler yazılır.<br />\r\n<br />\r\nKarar kaydı<br />\r\n<br />\r\nMADDE 11 &ndash; (1) Mahkemece verilen kararlara ilişkin bilgilerin tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Karar kaydı; karar sıra numarası, karar tarihi, dosya esas sıra numarası, kararı veren h&acirc;kim veya daire başkanı ve &uuml;yelerin ad ve soyadları ile sicil numaraları, tarafların, varsa kanun&icirc; temsilcileri ile vekillerin ad ve soyadları, karar sonucu ve davanın konusu s&uuml;tunlarını ihtiva eder.<br />\r\n<br />\r\n(3) Dava sonunda esas, hakem, tereke, değişik iş dosya t&uuml;rlerinden verilen h&uuml;k&uuml;m ve kararlar, tarih sırası ve sıra numarası ile elektronik ortamda g&ouml;r&uuml;nt&uuml;lenir.<br />\r\n<br />\r\nİstinaf kaydı<br />\r\n<br />\r\nMADDE 12 &ndash; (1) Mahkemece verilen h&uuml;k&uuml;mler aleyhine yapılan istinaf başvurularına ilişkin olarak tutulan kayıttır.<br />\r\n<br />\r\n(2) İstinaf kaydı; sıra numarası, dosya esas sıra numarası, istinaf yoluna başvuran tarafın sıfatı, varsa kanun&icirc; temsilcileri ile vekillerin ad ve soyadları, aleyhine istinaf yoluna başvurulan tarafın sıfatı, adı ve soyadı, istinaf dilek&ccedil;e tarihi, davanın nev&rsquo;i, karar tarihi ve numarası, aleyhine istinaf yoluna başvurulana tebliğ tarihi, istinaf şartlarının yerine getirilip getirilmediği, dosyanın hangi b&ouml;lge adliye mahkemesinin hangi hukuk dairesine g&ouml;nderildiği, g&ouml;nderilme tarihi, dosyanın istinaf incelemesinden d&ouml;nd&uuml;ğ&uuml; tarih ve neticesi ile d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nTemyiz kaydı<br />\r\n<br />\r\nMADDE 13 &ndash; (1) Mahkemece veya ilgili hukuk dairesince verilen h&uuml;k&uuml;mler aleyhine yapılan temyiz başvurularına ilişkin olarak tutulan kayıttır.<br />\r\n<br />\r\n(2) Temyiz kaydı; sıra numarası, dosya esas sıra numarası, temyiz yoluna başvuranın taraf sıfatı, adı ve soyadı, aleyhine temyiz yoluna başvurulanın taraf sıfatı, adı ve soyadı, temyiz dilek&ccedil;e tarihi, davanın nev&rsquo;i, karar tarih ve numarası, aleyhine temyiz olunana tebliğ tarihi, temyiz şartlarının yerine getirilip getirilmediği, dosyanın Yargıtay&rsquo;ın hangi dairesine g&ouml;nderildiği, g&ouml;nderilme tarihi, dosyanın temyiz incelemesinden d&ouml;nd&uuml;ğ&uuml; tarih ve neticesi ile d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nİstinabe kaydı<br />\r\n<br />\r\nMADDE 14 &ndash; (1) Başka yer mahkemelerinden veya ilgili hukuk dairelerinden dava ve işlerle ilgili olarak g&ouml;nderilen istinabe taleplerine ilişkin bilgilerin tutulduğu kayıttır.<br />\r\n<br />\r\n(2) İstinabe kaydı; sıra numarası, m&uuml;racaat ve tevdi tarihi, dosyadaki taraflar, talebin nev&rsquo;i, tarihi, g&ouml;nderen mahkeme veya hukuk dairesinin adı ve dosya esas sıra numarası, muamele neticesi, iade tarihi ve d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nDeğişik işler kaydı<br />\r\n<br />\r\nMADDE 15 &ndash; (1) Yukarıda sayılan kayıtlarda tutulması gerekmeyen ihtiyat&icirc; tedbir, delil tespiti gibi karar ve işlemlerin tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Değişik işler kaydı; sıra numarası, talep tarihi, talep eden ile varsa karşı tarafın, adı ve soyadı ya da unvanı, T.C. kimlik numarası, varsa ticaret sicil numarası veya merkezi t&uuml;zel kişi kimlik numarası, talep konusu, karar numarası, kararın &ouml;zeti ve tarihi ile d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nDuruşma g&uuml;nleri kaydı<br />\r\n<br />\r\nMADDE 16 &ndash; (1) Mahkemelerin ve hukuk dairelerinin iş durumlarına g&ouml;re duruşma yapılacak g&uuml;n ve saatlerin bir sıra d&acirc;hilinde raporlanabildiği kayıttır.<br />\r\n<br />\r\n(2) Duruşma g&uuml;nleri kaydı; sıra numarası, dosya esas sıra numarası, duruşma g&uuml;n ve saati, tarafların ad ve soyadları, davanın t&uuml;r&uuml;, duruşmanın bırakıldığı tarih, karar numarası ve işlem sonucu s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nKeşif g&uuml;nleri kaydı<br />\r\n<br />\r\nMADDE 17 &ndash; (1) Mahkemelerin ve hukuk dairelerinin iş durumlarına g&ouml;re keşif yapılacak g&uuml;n ve saatlerin bir sıra d&acirc;hilinde tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Keşif kaydı; sıra numarası, dosya esas sıra numarası, keşif g&uuml;n ve saati, davanın t&uuml;r&uuml;, tarafların ad ve soyadları ve işlem sonucu s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nTaşra istinaf veya temyiz kaydı<br />\r\n<br />\r\nMADDE 18 &ndash; (1) Başka yer mahkemeleri veya hukuk daireleri ile ilgili olarak g&ouml;nderilecek istinaf veya temyiz başvurularının tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Taşra istinaf veya temyiz kaydı; sıra numarası, g&ouml;nderilecek yer mahkemesi veya hukuk dairesinin adı, evrak &ouml;zeti, dilek&ccedil;e tarihi, dosya numarası, d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nSatış talep edilen ortaklığın giderilmesi dosyalarının kaydına mahsus esas kaydı<br />\r\n<br />\r\nMADDE 19 &ndash; (1) Satış talep edilen ortaklığın giderilmesi dosyalarının kaydının ve ge&ccedil;irdiği safahatın tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Bu kayıt; sıra numarası, satış kararını veren mahkemenin adı, dosya esas sıra numarası, karar tarihi, karar numarası, hissedarların ad ve soyadları, satış talebi tarihi, satış memurunun adı ve soyadı, dosyayı aldığı tarih, satışı m&uuml;teakip dosyanın mahkemeye tevdi tarihi, banka hesap numarası, satıştan elde edilen paranın hissedarlara tevziine dair muameleler ve netice s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nOrtaklığın giderilmesi satış paralarının kaydına mahsus kasa kaydı<br />\r\n<br />\r\nMADDE 20 &ndash; (1) Satıştan elde edilen para i&ccedil;in tutulan kayıttır. Hak sahiplerine dağıtılacak bu paralar i&ccedil;in bankada ayrı bir hesap a&ccedil;tırılır.<br />\r\n<br />\r\n(2) Bu kayıt; sıra numarası, işlem t&uuml;r&uuml;, işlem tarihi, parayı yatıran veya &ccedil;ekenin adı ve soyadı, sicil numarası, birim adı, dosya esas sıra numarası, makbuz numarası, miktar s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\n(3) İşlem t&uuml;r&uuml; s&uuml;tununa işlemin tahsil&acirc;t veya reddiyat olduğu yazılır.<br />\r\n<br />\r\nTereke esas kaydı<br />\r\n<br />\r\nMADDE 21 &ndash; (1) T&uuml;rk Medeni Kanununun Velayet, Vesayet ve Miras H&uuml;k&uuml;mlerinin Uygulanmasına İlişkin T&uuml;z&uuml;k Kapsamında Tutulacak Defterler, &Ouml;zel K&uuml;t&uuml;k, Dosyalar, Tutanaklar ve Diğer Evrakın D&uuml;zenlenmesine Dair Y&ouml;netmelik gereğince tutulan ve ayrıca tereke mallarının korunması, defterinin tutulması ve hak sahiplerine ge&ccedil;mesini sağlamak i&ccedil;in tutulan kayıttır.<br />\r\n<br />\r\n(2) Tereke esas kaydı; sıra numarası, varsa ilgili dosya esas numarası, davanın a&ccedil;ıldığı tarih, başvuranın, miras bırakanın ve varsa veli, vasi veya kayyımın adı ve soyadı, adresleri ve T.C. kimlik numaraları, başvuru tarihi, miras bırakanın &ouml;l&uuml;m tarihi, h&uuml;km&uuml;n sonucu, tarih ve numarası, kanun yollarına başvurma s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nTereke karar kaydı<br />\r\n<br />\r\nMADDE 22 &ndash; (1) Tereke dosyalarına ilişkin mahkemece verilen kararların tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Tereke karar kaydı; sıra numarası, karar tarihi, dosya esas sıra numarası, kararı veren h&acirc;kimin adı ve soyadı ile sicil numarası, başvuranın, miras bırakanın ve varsa veli, vasi veya kayyımın adı ve soyadı, karar sonucu ve davanın konusu s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nTereke kasa kaydı<br />\r\n<br />\r\nMADDE 23 &ndash; (1) Terekeye ait bankaya yatırılan ve &ccedil;ekilen para i&ccedil;in tutulan kayıttır. Bu para i&ccedil;in bankada her dosya bakımından ayrı bir hesap a&ccedil;tırılır.<br />\r\n<br />\r\n(2) Bu kayıt; sıra numarası, işlem t&uuml;r&uuml;, işlem tarihi, parayı yatıran veya &ccedil;ekenin adı ve soyadı, sicil numarası, birim adı, dosya esas sıra numarası, makbuz numarası, miktar s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nMirası ret kaydı<br />\r\n<br />\r\nMADDE 24 &ndash; (1) Miras&ccedil;ıların mirası ret etmesi halinde tutulan kayıttır.<br />\r\n<br />\r\n(2) Mirası ret kaydı; miras bırakanın adı ve soyadı, &ouml;l&uuml;m tarihi, mirası ret i&ccedil;in başvuru tarihi, reddedenin adı ve soyadı, T.C. kimlik numarası, reddedenin ik&acirc;met adresi, ret beyanını i&ccedil;eren tutanağın tarihi, tereke esas numarası, miras&ccedil;ılık belgesini veren mahkemenin adı ile esas ve karar numarası ve d&uuml;ş&uuml;nceler s&uuml;tununu i&ccedil;erir.<br />\r\n<br />\r\n(3) Reddeden miras&ccedil;ıya mirası ret ettiğine dair belge verilmiş ise bu husus d&uuml;ş&uuml;nceler s&uuml;tununda belirtilir.<br />\r\n<br />\r\n(4) Reddeden miras&ccedil;ıya talebi halinde, mirası ret kaydı numarasını i&ccedil;eren, mahkeme m&uuml;hr&uuml; ile m&uuml;h&uuml;rlenmiş ve zabıt k&acirc;tibi tarafından imzalanmış mirası reddettiğine ilişkin bir belge verilir.<br />\r\n<br />\r\nPosta mutemet kaydı<br />\r\n<br />\r\nMADDE 25 &ndash; (1) Havale yoluyla gelen paraya ilişkin bilgilerin tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Bu kayıt; havalenin &ccedil;ıkış yeri, numarası, tarihi, cinsi, miktarı, g&ouml;nderenin adı ve soyadı, dosya numarası, posta g&ouml;revlisinin adı ve soyadı, kasa kayıt tarihi ve numarası, tahsil&acirc;t makbuzunun tarihi ve numarası s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\n(3) Elektronik ortamda tutulan kaydın &ccedil;ıktısı alınır, ilgili s&uuml;tun imzalatıldıktan sonra ayrı bir kartonda saklanır.<br />\r\n<br />\r\nTevzi kaydı<br />\r\n<br />\r\nMADDE 26 &ndash; (1) Tevzi b&uuml;rosunca dava veya işlerin hangi mahkemeye veya hukuk dairesine g&ouml;nderildiğine ilişkin tutulan kayıttır.<br />\r\n<br />\r\n(2) Tevzi kaydı; sıra numarası, dağıtım yapılan mahkemenin veya hukuk dairesinin adı, dosyanın esas numarası ile esas numarasının verildiği tarih ve saati, tarafların adı, soyadı veya unvanı ve davanın konusu s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nZimmet kaydı<br />\r\n<br />\r\nMADDE 27 &ndash; (1) Mahkemelerden veya hukuk dairelerinden &ccedil;eşitli mercilere g&ouml;nderilen evrakın işlendiği kayıttır.<br />\r\n<br />\r\n(2) Zimmet kaydı; sıra numarası, evrak numarası, g&ouml;nderildiği daire, alındığı tarih, evrakı alanın adı ve soyadı ve evrakı alanın imzası s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\n(3) Elektronik ortamda tutulan kaydın &ccedil;ıktısı alınır, ilgili s&uuml;tun imzalatıldıktan sonra ayrı bir kartonda saklanır.<br />\r\n<br />\r\nHar&ccedil; tahsil m&uuml;zekkeresi zimmet kaydı<br />\r\n<br />\r\nMADDE 28 &ndash; (1) Mahkemelerden veya hukuk dairelerinden g&ouml;nderilen har&ccedil; tahsil m&uuml;zekkerelerinin işlendiği kayıttır.<br />\r\n<br />\r\n(2) Zimmet kaydı; sıra numarası, evrak numarası, g&ouml;nderildiği daire, alındığı tarih, evrakı alanın adı ve soyadı ve evrakı alanın imzası s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\n(3) Elektronik ortamda tutulan kaydın &ccedil;ıktısı alınır, ilgili s&uuml;tun imzalatıldıktan sonra ayrı bir kartonda saklanır.<br />\r\n<br />\r\nVakıf tescil kaydı<br />\r\n<br />\r\nMADDE 29 &ndash; (1) Mahkemece vakfın sicile tesciline ilişkin tutulan kayıttır.<br />\r\n<br />\r\n(2) Vakıf tescil kaydı; sıra numarası, vakfın adı, sicil tarihi ve tescili yapan taraf, lehine tescili yapılan taraf, tescil yapılma sebebi, tescil tarihi ve d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nVesayet ad kaydı<br />\r\n<br />\r\nMADDE 30 &ndash; (1) Vesayet altına alınan veya kendilerine kayyım tayin olunanların alfabetik sırayla adlarının tutulduğu defterdir.<br />\r\n<br />\r\n(2) Vesayet ad kaydı; sıra numarası, esas numarası, karar numarası, kısıtlı, vasi, kayyım veya gaibin adı ve soyadı, T.C. kimlik numaraları, işi, adresi, ikametg&acirc;hı, vesayet altına alınma sebebi, vesayet altına alındığı tarih, vasi veya kayyımın tayin edildiği tarih, g&ouml;reve başlayış tarihi, g&ouml;revin bitiş tarihi, malın devir ve teslim edildiği tarih ve d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nKıymetli evrak ve eşya kaydı<br />\r\n<br />\r\nMADDE 31 &ndash; (1) Mahkeme, hukuk dairesi, tereke h&acirc;kimliği, ortaklığın giderilmesi satış memurluğu veznesince alınan d&ouml;viz cinsinden paralar ile diğer kıymetli evrak ve eşyaların işlendiği kayıttır.<br />\r\n<br />\r\n(2) Kıymetli evrak eşya kaydı; sıra numarası, dosya esas sıra numarası, verilen makbuzun tarihi ve numarası, kıymetli evrakın sahibinin adı ve soyadı, kıymetli evrakın cinsi, &acirc;deti, numarası, takdir edilen kıymeti, satılmış ve paraya &ccedil;evrilmişse bedeli, aynen iade olmuşsa iade tarihi, iade alanın adı, soyadı ve imzası, nerede saklandığı ve d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nSosyal inceleme-g&ouml;r&uuml;şme kaydı<br />\r\n<br />\r\nMADDE 32 &ndash; (1) Mahkemece veya hukuk dairesince sosyal incelemecilerin raporlarına ilişkin tutulan kayıttır.<br />\r\n<br />\r\n(2) Sosyal inceleme-g&ouml;r&uuml;şme kaydı; sıra numarası, ilgili dosya esas numarası, tarafların ad ve soyadları, uzmanların ad ve soyadları, dosyanın veriliş tarihi, dosyanın geldiği tarih ve rapor &ouml;zeti s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nMuhabere kaydı<br />\r\n<br />\r\nMADDE 33 &ndash; (1) Yukarıdaki maddelerde d&uuml;zenlenen kayıtlara işlenmesi gerekenler dışında gelen ve giden evrak ile başka yer mahkemeleri veya diğer kurumlara g&ouml;nderilmek &uuml;zere verilen dilek&ccedil;elere ilişkin bilgilerin tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Muhabere kaydı; sıra numarası, evrakın tarih veya numarası, g&ouml;nderilen veya g&ouml;nderen mahkeme, hukuk dairesi veya kurumun adı, geliş veya g&ouml;nderilme tarihi, evrakın &ouml;zeti, d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nHakem kararları saklama kaydı<br />\r\n<br />\r\nMADDE 34 &ndash; (1) Hukuk Muhakemeleri Kanununun 436 ncı maddesinin &uuml;&ccedil;&uuml;nc&uuml; fıkrası uyarınca mahkemece saklanacak hakem kararlarına ilişkin bilgilerin tutulduğu kayıttır.<br />\r\n<br />\r\n(2) Hakem kararları saklama kaydı, sıra numarası, kararı veren hakem veya hakem kurulu &uuml;yelerinin ad ve soyadları, tarafların, varsa kanun&icirc; temsilcileri ile vekillerin ad ve soyadları, T.C. kimlik numarası, davanın konusu, nihai karar ve tarihi ile d&uuml;ş&uuml;nceler s&uuml;tunlarını i&ccedil;erir.<br />\r\n<br />\r\nYeni kayıt eklenmesi ve sorgulama<br />\r\n<br />\r\nMADDE 35 &ndash; (1) Bu Y&ouml;netmelikte d&uuml;zenlenen kayıtlara gerek g&ouml;r&uuml;ld&uuml;ğ&uuml;nde Bakanlık tarafından yeni kayıtlar veya mevcut kayıtlara yeni s&uuml;tunlar eklenebilir.<br />\r\n<br />\r\n(2) Bu kayıtlardaki verilerin bir veya birka&ccedil;ı bir arada sorgulanıp raporlanabilir.<br />\r\n<br />\r\nİKİNCİ B&Ouml;L&Uuml;M<br />\r\n<br />\r\nDavanın A&ccedil;ılması ve İşlerin Kaydı<br />\r\n<br />\r\nDavanın a&ccedil;ılması<br />\r\n<br />\r\nMADDE 36 &ndash; (1) Dava dilek&ccedil;esi, tevzi b&uuml;rosu, &ouml;n b&uuml;ro veya tevzi işiyle g&ouml;revlendirilen yazı işleri personeline teslim edilir.<br />\r\n<br />\r\n(2) Dava dilek&ccedil;esi, dava harca tabi ise har&ccedil; ve gider avansı, harca tabi değilse gider avansı tahsil edildikten sonra tevzi edilir ve tevzi formunun bir &ouml;rneği başvuru sahibine verilir.<br />\r\n<br />\r\n(3) Tevzi işlemi tamamlandığında, dosya hangi mahkemeye g&ouml;nderilmiş ise o mahkemenin esas kaydından numara alır ve sistem tarafından aynı anda tevzi formu d&uuml;zenlenir. Tevzi formu, dava veya işlerin hangi mahkemeye veya hukuk dairesine g&ouml;nderildiğini g&ouml;steren ve başvuru sahibine verilen alındı belgesidir. Tevzi formu, dağıtım yapılan mahkemenin adını, dosyanın esas numarası ile esas numarasının verildiği tarih ve saati, dosya t&uuml;r&uuml;n&uuml;, tarafların ad ve soyadlarını, davanın konusunu ve varsa ilişkili dosya numarasını i&ccedil;erir.<br />\r\n<br />\r\n(4) Başka yer tevzi b&uuml;rosundan a&ccedil;ılan davalarda da yukarıdaki fıkralar uygulanır. Başka yer tevzi b&uuml;rosu, ilgili mahkemeye doğrudan tevzi yapar ve teslim aldığı evrakı elektronik ortama aktarır, fizik&icirc; evrakı da gecikmeksizin ilgili mahkemeye g&ouml;nderir. Posta ve havale masrafı d&uuml;ş&uuml;ld&uuml;kten sonra, gider avansından kalan miktar ilgili mahkemenin hesabına aktarılır.<br />\r\n<br />\r\n(5) Dava, dava dilek&ccedil;esinin tevzi edilerek kaydedildiği tarihte a&ccedil;ılmış sayılır.<br />\r\n<br />\r\n(6) Herhangi bir nedenle elektronik ortamda işlem yapılamaması halinde durum bir tutanakla tespit edilir ve işlem fiziki ortamda yapılır. Elektronik sistem a&ccedil;ıldığında fizik&icirc; ortamda yapılan işlemler gecikmeksizin elektronik ortama aktarılır. Bu durumda dava, s&ouml;z konusu tutanağın d&uuml;zenlendiği tarihte a&ccedil;ılmış sayılır.<br />\r\n<br />\r\n(7) Fiziksel ortamda gelen t&uuml;m belgeler derhal elektronik ortama aktarılır.<br />\r\n<br />\r\n(8) Ger&ccedil;ek kişilerin UYAP Vatandaş Bilgi Sistemi &uuml;zerinden, t&uuml;zel kişi temsilcilerinin UYAP Kurum Bilgi Sistemi &uuml;zerinden dava a&ccedil;abilmeleri i&ccedil;in elektronik imza sahibi olmaları gerekir. Ger&ccedil;ek ve t&uuml;zel kişilerin elektronik ortamda a&ccedil;acakları davaların yargılama har&ccedil;ları ve gider avansı elektronik ortamda mahkeme veznesinin bağlı olduğu banka hesabına aktarılır. Bu işlemlerin kredi kartı ve benzeri &ouml;deme ara&ccedil;ları ile de yapılması sağlanabilir. Dava, dilek&ccedil;enin sisteme kaydedildiği tarihte a&ccedil;ılmış sayılır. İşlem sonucunda başvuru sahibinin elektronik ortamda erişebileceği bir tevzi formu oluşturulur.<br />\r\n<br />\r\n(9) Taraf vekillerince UYAP &uuml;zerinden g&uuml;venli elektronik imza ile dava a&ccedil;ılabilir. Bu işler i&ccedil;in ayrıca el &uuml;r&uuml;n&uuml; imzalı belge istenmez. Avukatların UYAP Avukat Bilgi Sistemi &uuml;zerinden dava a&ccedil;abilmeleri i&ccedil;in g&uuml;venli elektronik imza sahibi olmaları gerekir. Yargılama har&ccedil;ları ve gider avansı davanın a&ccedil;ılması esnasında avukat tarafından elektronik ortamda mahkeme veznesi hesabına aktarılır. Ayrıca bu işlemlerin baro kartı veya kredi kartı gibi &ouml;deme ara&ccedil;larıyla yapılması sağlanabilir. Dava, dilek&ccedil;enin sisteme kaydedildiği tarihte a&ccedil;ılmış sayılır. İşlem sonucunda başvuru sahibinin elektronik ortamda erişebileceği bir tevzi formu oluşturulur.<br />\r\n<br />\r\n(10) Dava, fiziksel ortamda mesai saati, elektronik ortamda ise saat 00:00&rsquo;a kadar a&ccedil;ılabilir.<br />\r\n<br />\r\nBasit yargılama usul&uuml;nde dava ve cevap dilek&ccedil;esinin verilmesi<br />\r\n<br />\r\nMADDE 37 &ndash; (1) Dava a&ccedil;ılması ve davaya cevap verilmesi dilek&ccedil;eyle olur.<br />\r\n<br />\r\n(2) Basit yargılama usul&uuml;nde, dava ve cevap dilek&ccedil;eleri UYAP Bilgi Sistemlerinde yer alan dava ve cevap dilek&ccedil;esi formları doldurulmak suretiyle de verilebilir.<br />\r\n<br />\r\n(3) Dava dilek&ccedil;esi formunda aşağıdaki hususlar bulunur:<br />\r\n<br />\r\na) Mahkemenin adı.<br />\r\n<br />\r\nb) Davacı ile davalının adı, soyadı ve adresleri.<br />\r\n<br />\r\nc) Davacının T.C. kimlik numarası ile varsa ticaret sicil numarası veya t&uuml;zel kişi kimlik numarası.<br />\r\n<br />\r\n&ccedil;) Varsa tarafların kanun&icirc; temsilcilerinin ve davacı vekilinin adı, soyadı ve adresleri.<br />\r\n<br />\r\nd) Davanın konusu.<br />\r\n<br />\r\ne) Davacının iddiasının dayanağı olan b&uuml;t&uuml;n vakıaların sıra numarası altında a&ccedil;ık &ouml;zetleri.<br />\r\n<br />\r\nf) İddia edilen her bir vakıanın hangi delillerle ispat edileceği.<br />\r\n<br />\r\ng) Dayanılan hukuk&icirc; sebepler.<br />\r\n<br />\r\nğ) A&ccedil;ık bir şekilde talep sonucu.<br />\r\n<br />\r\nh) Davacının, varsa kanun&icirc; temsilcisinin veya vekilinin imzası.<br />\r\n<br />\r\n(4) Cevap dilek&ccedil;esi formunda aşağıdaki hususlar bulunur:<br />\r\n<br />\r\na) Mahkemenin adı.<br />\r\n<br />\r\nb) Davacı ile davalının adı, soyadı ve adresleri; davalı yurt dışında ise a&ccedil;ılan dava ile ilgili işlemlere esas olmak &uuml;zere yurt i&ccedil;inde g&ouml;stereceği bir adres.<br />\r\n<br />\r\nc) Davalının T.C. kimlik numarası ile varsa ticaret sicil numarası veya t&uuml;zel kişi kimlik numarası.<br />\r\n<br />\r\n&ccedil;) Varsa, tarafların kanun&icirc; temsilcilerinin ve davacı vekilinin adı, soyadı ve adresleri.<br />\r\n<br />\r\nd) Davalının savunmasının dayanağı olan b&uuml;t&uuml;n vakıaların sıra numarası altında a&ccedil;ık &ouml;zetleri.<br />\r\n<br />\r\ne) Savunmanın dayanağı olarak ileri s&uuml;r&uuml;len her bir vakıanın hangi delillerle ispat edileceği.<br />\r\n<br />\r\nf) Dayanılan hukuk&icirc; sebepler.<br />\r\n<br />\r\ng) A&ccedil;ık bir şekilde talep sonucu.<br />\r\n<br />\r\nğ) Davalının veya varsa kanun&icirc; temsilcisinin yahut vekilinin imzası.<br />\r\n<br />\r\n(5) Dava veya cevap dilek&ccedil;esi formu, fizik&icirc; ortamda tevzi b&uuml;rosu ya da &ouml;n b&uuml;rodan temin edilerek doldurulması suretiyle verilebilir.<br />\r\n<br />\r\n(6) Dava ve cevap dilek&ccedil;esi formlarının elektronik ortamda verilebilmesi i&ccedil;in g&uuml;venli elektronik imza sahibi olunması gerekir. Aksi halde dava ve cevap dilek&ccedil;esi formları elektronik ortamda doldurulduktan sonra alınacak &ccedil;ıktısı el ile imzalanır.<br />\r\n<br />\r\nDava ve işlerin tevzii<br />\r\n<br />\r\nMADDE 38 &ndash; (1) Dava, istinabe evrakı ve diğer işler puanlama y&ouml;ntemine g&ouml;re mahkemelere tevzi edilir. Her mahkemeye eşit puanda dava ve iş g&ouml;nderilmesi sağlanır. Puanlama kriterleri H&acirc;kimler ve Savcılar Y&uuml;ksek Kurulunun g&ouml;r&uuml;ş&uuml; alınarak Bakanlık tarafından belirlenir.<br />\r\n<br />\r\n(2) Dava ve işlerin tevziinden kaynaklanan uyuşmazlıklar başvuru &uuml;zerine ilgisine g&ouml;re, adli yargı ilk derece mahkemesi adalet komisyonu başkanı veya b&ouml;lge adliye mahkemesi adalet komisyonu başkanı tarafından tevzi kriterleri esas alınarak karara bağlanır.<br />\r\n<br />\r\nHavale, dilek&ccedil;e ve belgelerin alınması<br />\r\n<br />\r\nMADDE 39 &ndash; (1) Dava ile ilgili mahkemeye veya hukuk dairesine sunulan her t&uuml;rl&uuml; dilek&ccedil;e ve belge &ouml;n b&uuml;ro veya yazı işlerinde g&ouml;revli personele teslim edilir. Dilek&ccedil;e veya belgenin alındığına ve elektronik ortama aktarıldığına dair başvuru sahibine &uuml;cretsiz olarak bir alındı belgesi verilir. Bu belge aynı zamanda havale yerine ge&ccedil;er.<br />\r\n<br />\r\n(2) Fiziken teslim alınıp elektronik ortama aktarılan veya doğrudan elektronik ortamda gelen dilek&ccedil;e veya belge, h&acirc;kim veya g&ouml;revlendireceği personel tarafından incelendikten sonra dosyasına aktarılır.<br />\r\n<br />\r\nTensip tutanağı<br />\r\n<br />\r\nMADDE 40 &ndash; (1) Davanın a&ccedil;ılmasından sonra, dilek&ccedil;eler aşamasının başında her dosya i&ccedil;in bir tensip tutanağı d&uuml;zenlenir. Tensip tutanağında yer alacak hususlar h&acirc;kimin takdirinde olmak kaydıyla; tensip tutanağının başına mahkemenin adı, mahkeme &ccedil;eşitli sıfatlarla g&ouml;rev yapıyorsa hangi sıfatla g&ouml;rev yaptığı, h&acirc;kim veya h&acirc;kimlerin ve zabıt k&acirc;tibinin ad ve soyadları ile sicil numaraları, tarafların kimlikleri ile T.C. kimlik numaraları, varsa kanun&icirc; temsilcileri ve vekillerinin ad ve soyadları ile adresleri yazıldıktan sonra aşağıdaki hususlara yer verilebilir.<br />\r\n<br />\r\na) Dava dilek&ccedil;esinin Hukuk Muhakemeleri Kanununun 119 uncu maddesinde d&uuml;zenlenen unsurları taşıyıp taşımadığının tespiti ile eksiklik bulunması halinde ne gibi işlemin yapıldığı.<br />\r\n<br />\r\nb) Davanın t&uuml;r&uuml;.<br />\r\n<br />\r\nc) Davanın tabi olduğu yargılama usul&uuml;.<br />\r\n<br />\r\n&ccedil;) Karşılıklı dilek&ccedil;elerin ve eklerinin tebliği ile gerekli işlemlerin yapılması.<br />\r\n<br />\r\nd) Tarafların, Hukuk Muhakemeleri Kanununun 121 inci maddesi ve 129 uncu maddesinin ikinci fıkrası gereğince delil olarak g&ouml;sterdikleri belgeleri dilek&ccedil;elerine ekleyerek vermeleri ile başka yerden getirilecek belgelere ilişkin gerekli bilgileri vermeleri, davacının delilleri i&ccedil;in gider avansının kullanılacağı, davalının gerekiyorsa tespit edilecek delil avansını da yatırması, tarafların bu hususları yerine getirmedikleri takdirde, basit yargılama usul&uuml;ne ilişkin h&uuml;k&uuml;mler saklı olmak &uuml;zere Hukuk Muhakemeleri Kanununun 140 ıncı maddesinin beşinci fıkrası gereğince ancak &ouml;n inceleme aşamasında bu eksiklikleri tamamlayabilecekleri, aksi halde bu delillerinden vazge&ccedil;miş sayılacakları.<br />\r\n<br />\r\ne) Dilek&ccedil;elerin tamamlanmasından sonra &ouml;n incelemenin duruşmalı yapılıp yapılmayacağına karar verileceği, &ouml;n incelemenin duruşmalı yapılmasına karar verilmesi halinde duruşma tarihinin dilek&ccedil;elerin tamamlanmasından sonra belirleneceği ve avanstan karşılanarak bildirileceği.<br />\r\n<br />\r\nf) &Ouml;n incelemede yargılamaya devam edilmesine karar verilmesi halinde, &ouml;n incelemenin sonunda ya da daha sonra tahkikat duruşması i&ccedil;in g&uuml;n verileceği.<br />\r\n<br />\r\ng) Dosyanın durumuna g&ouml;re gerek g&ouml;r&uuml;len diğer hususlar.<br />\r\n<br />\r\n(2) Tensip tutanağının d&uuml;zenlenmesinden sonra ilgili her aşamada gerekli kararlar verilerek işlemler yerine getirilir.<br />\r\n<br />\r\n&Ouml;n inceleme<br />\r\n<br />\r\nMADDE 41 &ndash; (1) Dilek&ccedil;eler aşaması tamamlandıktan sonra &ouml;n inceleme aşamasına ge&ccedil;ilir. &Ouml;n inceleme aşamasının sonunda aşağıdaki hususları i&ccedil;eren bir &ouml;n inceleme tutanağı d&uuml;zenlenir.<br />\r\n<br />\r\na) &Ouml;n incelemenin duruşmalı yapılıp yapılmadığı, &ouml;n inceleme duruşmalı yapılıyorsa duruşmaya taraflardan hangisinin katıldığı ya da mazeretinin bulunup bulunmadığı.<br />\r\n<br />\r\nb) Dava şartlarının mevcut olup olmadığı, eksiklik varsa ne t&uuml;r işlem yapıldığı.<br />\r\n<br />\r\nc) İlk itiraz bulunup bulunmadığı ve varsa ne t&uuml;r işlem yapıldığı.<br />\r\n<br />\r\n&ccedil;) Davacının iddialarının ve bunların dayanaklarının &ouml;zetle neler olduğu.<br />\r\n<br />\r\nd) Davalının savunmalarının ve bunların dayanaklarının &ouml;zetle neler olduğu.<br />\r\n<br />\r\ne) Tarafların uyuşmazlık noktalarının ana başlıklarıyla nelerden ibaret bulunduğu.<br />\r\n<br />\r\nf) Tarafların sunmadıkları belgelerin neler olduğu, dilek&ccedil;elerinde belirttikleri t&uuml;m belgeleri sunup sunmadıkları, ayrıca başka yerden getirilecek delillere ilişkin bilgi vermedikleri bir hususun bulunup bulunmadığı ve bu konuda ne şekilde işlem yapıldığı, bu aşamada karar verilmişse gerekli delil avansını yatırmaları.<br />\r\n<br />\r\ng) &Ouml;n incelemede yapılması gereken diğer işlemlerin yapılıp yapılmadığı.<br />\r\n<br />\r\nğ) İddia ya da savunmasını genişleten taraf olup olmadığı, bu konuda karşı tarafın a&ccedil;ık muvafakatinin bulunup bulunmadığı.<br />\r\n<br />\r\nh) Tarafların sulhe teşvik edildikleri ve sonu&ccedil;larının ne olduğu.<br />\r\n<br />\r\nı) Eğer &ouml;n inceleme duruşması sonunda tahkikat i&ccedil;in duruşma g&uuml;n&uuml; verilebiliyor ise duruşma g&uuml;n&uuml;, duruşma g&uuml;n&uuml; verilemiyor ise tahkikat i&ccedil;in duruşma g&uuml;n&uuml;n&uuml;n sonradan bildirileceği ya da duruşma g&uuml;n&uuml; vermeden tahkikata ge&ccedil;ileceği.<br />\r\n<br />\r\n(2) &Ouml;n inceleme tamamlanmadan tahkikata ge&ccedil;ilemez ve tahkikat işlemleri yapılamaz. Basit yargılama usul&uuml;nde &ouml;n inceleme ile tahkikat duruşması birlikte yapılabilir. Yazılı yargılama usul&uuml;nde de, h&acirc;kim &ouml;n incelemeyi tamamlayıp gerekli kararları verdikten sonra, aynı duruşmada tahkikata ge&ccedil;erek gerekli kararları verebilir. &Ouml;n inceleme ile tahkikat duruşmasının birlikte yapılması halinde, yargılama aşamaları tutanakta ayrı ayrı belirtilir.<br />\r\n<br />\r\n(3) H&acirc;kim tarafından Ek 1&rsquo;de yer alan &Ouml;rnek &Ouml;n İnceleme Kontrol Formu kullanılmak suretiyle &ouml;n inceleme tutanağı d&uuml;zenlenebileceği gibi &ouml;n inceleme tutanağı re&rsquo;sen de d&uuml;zenlenebilir. Ek 1&rsquo;de yer alan veya h&acirc;kim tarafından re&rsquo;sen oluşturulan form elektronik ortamda doldurulup g&uuml;venli elektronik imza ile imzalanarak kaydedilir ve &ouml;n inceleme tutanağına eklenir.<br />\r\n<br />\r\nDosyaların incelenmesi ve &ouml;rnek alma<br />\r\n<br />\r\nMADDE 42 &ndash; (1) Zabıt k&acirc;tibinin g&ouml;zetimi altında taraflar veya fer&rsquo;&icirc; m&uuml;dahil, dava dosyasını inceleyebilir. Dava ile ilgili olanlar da bunu ispatlamak kaydıyla h&acirc;kimin ya da bu konuda yetkilendirdiği yazı işleri m&uuml;d&uuml;r&uuml;n&uuml;n izniyle dosyayı inceleyebilir.<br />\r\n<br />\r\n(2) Avukatlar ve stajyerler, vek&acirc;letname olmaksızın dava ve takip dosyalarını zabıt k&acirc;tibinin g&ouml;zetiminde her zaman inceleyebilirler. İncelemenin yapıldığına dair d&uuml;zenlenen dosya inceleme tutanağı avukat veya avukat stajyeri ile zabıt k&acirc;tibi tarafından imzalanarak dosyasında saklanır.<br />\r\n<br />\r\n(3) Davacı, davalı, fer&rsquo;i m&uuml;dahil ve vekilleri dava dosyasındaki veya elektronik ortamdaki b&uuml;t&uuml;n tutanak ve belgelerin onaysız fotokopi ya da &ccedil;ıktısını har&ccedil;sız olarak alabilirler. Avukatların belge &ouml;rneği alabilmeleri i&ccedil;in vek&acirc;letnamelerinin bulunması zorunludur.<br />\r\n<br />\r\n(4) Gizli olarak saklanmasına karar verilen belge ve tutanakların incelenmesi h&acirc;kimin a&ccedil;ık iznine bağlıdır.<br />\r\n<br />\r\n(5) Taraf vekilleri UYAP Avukat Bilgi Sistemi vasıtasıyla dava dosyalarını inceleyebilir ve &ouml;rnek alabilirler.<br />\r\n<br />\r\n(6) Taraflar elektronik imza sahibi olmak koşuluyla UYAP Vatandaş veya Kurum Bilgi Sistemi vasıtasıyla tarafı oldukları dava ve işlere ait t&uuml;m evrakı inceleyebilir, &ouml;rnek alabilirler. Elektronik imza sahibi olmayan taraflar sadece dava ve işlerin kapak bilgilerine ulaşabilirler.<br />\r\n<br />\r\nDizi listesi<br />\r\n<br />\r\nMADDE 43 &ndash; (1) Dava dosyasının fiziksel olarak diğer bir mahkeme veya mercie g&ouml;nderilmesi gerektiğinde, zabıt k&acirc;tibince dosya i&ccedil;erisindeki her t&uuml;rl&uuml; belgeyi g&ouml;steren bir dizi listesi yapılır ve altı imzalanır. G&ouml;nderilen dosyanın son duruşma tutanağının bir &ouml;rneği ile dizi listesinin bir sureti alınarak ge&ccedil;ici bir dosya a&ccedil;ılır.<br />\r\n<br />\r\nDuruşma listesi<br />\r\n<br />\r\nMADDE 44 &ndash; (1) Duruşmalı işlerde mahkemenin adı, dosya esas sıra numarası, tarafların ad ve soyadları, duruşma tarih ve saati bir liste halinde duruşmadan &ouml;nce ilgililerin erişimine sunulur.<br />\r\n<br />\r\n&Uuml;&Ccedil;&Uuml;NC&Uuml; B&Ouml;L&Uuml;M<br />\r\n<br />\r\nYargılama Giderleri<br />\r\n<br />\r\nHar&ccedil;, gider avansı ve delil avansının &ouml;denmesi<br />\r\n<br />\r\nMADDE 45 &ndash; (1) Davacı, yargılama har&ccedil;ları ile her yıl Bakanlık&ccedil;a &ccedil;ıkarılacak gider avansı tarifesinde belirlenecek olan tutarı dava a&ccedil;arken mahkeme veznesine yatırmak zorundadır. Gider avansı, her t&uuml;rl&uuml; tebligat ve posta &uuml;cretleri, keşif giderleri, bilirkişi ve tanık &uuml;cretleri gibi giderler i&ccedil;in davacıdan alınan meblağı ifade eder.<br />\r\n<br />\r\n(2) Adli yardım talebiyle a&ccedil;ılan dava ve işlerde adli yardım konusunda bir karar verilinceye kadar har&ccedil;, gider ve delil avansı alınmaz. Kanunlardaki &ouml;zel h&uuml;k&uuml;mler saklıdır.<br />\r\n<br />\r\n(3) Gider avansının yeterli olmadığının dava sırasında anlaşılması halinde, mahkemece bu eksikliğin tamamlanması i&ccedil;in davacıya iki haftalık kesin s&uuml;re verilir. Dava şartı olan gider avansının yatırılmaması veya tamamlanmaması halinde, dava, dava şartı yokluğundan reddedilir.<br />\r\n<br />\r\n(4) Taraflardan her biri ikamesini talep ettiği delil i&ccedil;in mahkemece belirlenen avansı, verilen kesin s&uuml;re i&ccedil;inde yatırmak zorundadır. Delil avansı, tarafların dayandıkları delillerin giderlerini karşılamak &uuml;zere mahkemece belirlenen kesin s&uuml;re i&ccedil;inde &ouml;demeleri gereken meblağı ifade eder. Taraflar birlikte aynı delilin ikamesini talep etmişlerse, gereken gideri yarı yarıya avans olarak &ouml;derler. Taraflardan biri avans y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml;n&uuml; yerine getirmediğinde, diğer taraf bu avansı da yatırabilir. Delil avansını yatırmayan taraf, o delilin ikamesinden vazge&ccedil;miş sayılır. Tarafların &uuml;zerinde tasarruf edemeyecekleri dava ve işlerle, kanunlardaki &ouml;zel h&uuml;k&uuml;mler saklıdır.<br />\r\n<br />\r\n(5) Delil avansının &ouml;denmesine, h&acirc;kim tarafından dilek&ccedil;elerin verilmesi, &ouml;n inceleme aşaması veya tahkikatın başında karar verilir.<br />\r\n<br />\r\n(6) Har&ccedil;, Hukuk Muhakemeleri Kanununa g&ouml;re tahsil edilmesi gereken avans ve para cezaları elektronik ortamda tahsil edilebilir. Tahsil işlemi bankalar aracılığıyla olabileceği gibi Barokart, kredi kartı ve benzeri ara&ccedil;larla da yapılabilir.<br />\r\n<br />\r\n(7) Gider ve delil avansı olarak tahsil edilen paraların, mutad &ouml;demeler dikkate alınarak belirlenecek olan g&uuml;nl&uuml;k ihtiya&ccedil;tan fazlası Bakanlık&ccedil;a belirlenen bankaya yatırılır.<br />\r\n<br />\r\nHar&ccedil;ların ve para cezalarının tahsili<br />\r\n<br />\r\nMADDE 46 &ndash; (1) Har&ccedil;ların ve para cezalarının tahsili i&ccedil;in gereken m&uuml;zekkereler yazı işleri m&uuml;d&uuml;r&uuml; tarafından yazılır. Her yıl b&uuml;t&ccedil;e kanunu ile belirlenen terkin sınırının altında kalan har&ccedil;larla ilgili m&uuml;zekkere yazılmaz.<br />\r\n<br />\r\n(2) Har&ccedil; tahsil m&uuml;zekkereleri karar tarihinden itibaren en ge&ccedil; iki ay onbeş g&uuml;n i&ccedil;inde yazılmak zorundadır. 3402 sayılı Kadastro Kanununun 36 ncı maddesinin &uuml;&ccedil;&uuml;nc&uuml; fıkrası uyarınca har&ccedil; tahsil m&uuml;zekkereleri h&uuml;km&uuml;n kesinleştiği tarihten itibaren onbeş g&uuml;n i&ccedil;inde yazılır.<br />\r\n<br />\r\nYargılama giderlerinin iadesi<br />\r\n<br />\r\nMADDE 47 &ndash; (1) Yargılama gideri i&ccedil;in tahsil edilen paranın kullanılmayan kısmı h&uuml;km&uuml;n kesinleşmesinden sonra yazı işleri m&uuml;d&uuml;r&uuml; tarafından ilgilisine iade edilir. Hesap numarası bildirilmiş ise iade elektronik ortamda hesaba aktarmak suretiyle yapılır. Hesap numarası bildirilmemiş ise masrafı kalan paradan karşılanmak suretiyle PTT merkez ve işyerleri vasıtasıyla adreste &ouml;demeli olarak g&ouml;nderilir.<br />\r\n<br />\r\n(2) Ge&ccedil;ici hukuk&icirc; koruma talebi i&ccedil;in alınan yargılama giderinin kullanılmayan kısmı verilen karardan sonra talep &uuml;zerine iade edilir.<br />\r\n<br />\r\nD&Ouml;RD&Uuml;NC&Uuml; B&Ouml;L&Uuml;M<br />\r\n<br />\r\nKanun Yolu İşlemleri<br />\r\n<br />\r\nKanun yoluna başvuru işlemleri<br />\r\n<br />\r\nMADDE 48 &ndash; (1) Kanun yoluna başvuru dilek&ccedil;esi, &ouml;n b&uuml;ro veya yazı işlerinde g&ouml;revli personele teslim edilir.<br />\r\n<br />\r\n(2) Kanun yoluna başvuru dilek&ccedil;esi harca tabi değilse hemen, harca tabi ise har&ccedil; &ouml;dendikten sonra kaydedilir ve başvuru sahibine &uuml;cretsiz alındı belgesi verilir.<br />\r\n<br />\r\n(3) Alındı belgesi, kanun yolu dilek&ccedil;esinin sisteme kaydedilmesi &uuml;zerine verilen belgedir. Alındı belgesi, mahkemenin adını, dosyanın esas ve karar numarasını, karar tarihini, tarafların ve varsa m&uuml;dahillerin ad ve soyadlarını, davanın konusunu, başvurulan kanun yolu merciini, başvuru tarih ve saatini i&ccedil;erir.<br />\r\n<br />\r\n(4) Kanun yolu başvurusu, kanun yolu dilek&ccedil;esinin kaydedildiği tarihte yapılmış sayılır.<br />\r\n<br />\r\n(5) Başka yer mahkemesine verilen kanun yoluna başvuru dilek&ccedil;elerinde de yukarıdaki h&uuml;k&uuml;mler uygulanır. Başka yer yazı işleri m&uuml;d&uuml;r&uuml; veya g&ouml;revli personel teslim aldığı dilek&ccedil;e ve eklerini elektronik ortama aktarır, fizik&icirc; evrakı da gecikmeksizin ilgili mahkemeye g&ouml;nderir.<br />\r\n<br />\r\n(6) Herhangi bir nedenle elektronik ortamda işlem yapılamaması halinde durum bir tutanakla tespit edilir ve işlem fiziki ortamda yapılır. Elektronik sistem a&ccedil;ıldığında fizik&icirc; ortamda yapılan işlemler gecikmeksizin elektronik ortama aktarılır. Bu durumda kanun yolu başvuru dilek&ccedil;esi tutanağın d&uuml;zenlendiği tarihte verilmiş sayılır.<br />\r\n<br />\r\n(7) Fiziksel ortamda kanun yolu başvurusu mesai saatleri i&ccedil;inde yapılır.<br />\r\n<br />\r\n(8) Ger&ccedil;ek kişilerin UYAP Vatandaş Bilgi Sistemi &uuml;zerinden, t&uuml;zel kişi temsilcilerinin UYAP Kurum Bilgi Sistemi &uuml;zerinden kanun yolu başvuru dilek&ccedil;eleri g&ouml;nderebilmeleri i&ccedil;in elektronik imza sahibi olmaları gerekir. Ger&ccedil;ek ve t&uuml;zel kişiler elektronik ortamda yapacakları kanun yolu başvurusunun harcını elektronik ortamda mahkeme veznesinin bağlı olduğu banka hesabına aktarırlar. Kanun yolu başvurusu, dilek&ccedil;enin sisteme kaydedildiği tarihte yapılmış sayılır. İşlem sonucunda başvuru sahibinin elektronik ortamda erişebileceği bir alındı belgesi oluşturulur.<br />\r\n<br />\r\n(9) Taraf vekillerince UYAP &uuml;zerinden g&uuml;venli elektronik imza ile kanun yolu başvuru dilek&ccedil;esi g&ouml;nderilebilir. Bu işler i&ccedil;in ayrıca elle atılmış imzalı belge istenmez. Avukatların UYAP Avukat Bilgi Sistemi &uuml;zerinden kanun yolu başvuru dilek&ccedil;esi g&ouml;nderebilmeleri i&ccedil;in elektronik imza sahibi olmaları gerekir. Kanun yolu har&ccedil;ları avukat tarafından elektronik ortamda mahkeme veznesi hesabına aktarılır. Ayrıca bu işlemlerin Barokart veya kredi kartı gibi &ouml;deme ara&ccedil;larıyla yapılması sağlanabilir. Kanun yolu başvurusu, dilek&ccedil;enin sisteme kaydedildiği tarihte yapılmış sayılır. İşlem sonucunda başvuru sahibinin elektronik ortamda erişebileceği bir alındı belgesi oluşturulur.<br />\r\n<br />\r\n(10) Elektronik ortamda kanun yolu başvurusu saat 00:00&rsquo;a kadar yapılabilir.<br />\r\n<br />\r\n(11) Kanun yoluna başvurulan dava veya işler, g&ouml;revli daire doğru bir şekilde belirlendikten sonra kanun yolu formu ve dizi pusulası UYAP &uuml;zenden hazırlanarak ilgili mercie g&ouml;nderilir.<br />\r\n<br />\r\nBEŞİNCİ B&Ouml;L&Uuml;M<br />\r\n<br />\r\n&Ccedil;ekişmesiz Yargıda M&uuml;h&uuml;rleme, Deftere Ge&ccedil;irme ve Yemin Tutanağı<br />\r\n<br />\r\nD&uuml;zenlenmesi Usul&uuml;<br />\r\n<br />\r\nM&uuml;h&uuml;rleme<br />\r\n<br />\r\nMADDE 49 &ndash; (1) M&uuml;h&uuml;rleme, taşınır malların koruma altına alınması amacıyla bir yerde tutularak kullanılmasının &ouml;nlenmesi veya bir taşınmazın kullanılmasının, değiştirilmesinin &ouml;nlenmesi ya da belirli bir faaliyetin durdurulmasıdır.<br />\r\n<br />\r\n(2) Taşınır mallar deftere ge&ccedil;irildikten sonra bir torba veya kapalı bir yere konularak muhafaza altına alınır ve uygun ara&ccedil;larla m&uuml;h&uuml;rlenir.<br />\r\n<br />\r\n(3) Taşınmazlarda m&uuml;h&uuml;rleme işlemi, taşınmaza giriş ve &ccedil;ıkışın engellenmesi veya bir faaliyetin &ouml;nlenmesi şeklinde ger&ccedil;ekleştirilir.<br />\r\n<br />\r\n(4) M&uuml;h&uuml;rleme işlemleri yazı işleri m&uuml;d&uuml;r&uuml; veya g&ouml;revli personel tarafından yer, g&uuml;n ve saat belirtilip tutanağa bağlanarak imzalanır.<br />\r\n<br />\r\nDefter tutma<br />\r\n<br />\r\nMADDE 50 &ndash; (1) Defter tutma, taşınır ve taşınmaz mallar ile bor&ccedil;, alacak ve hakların kayıt altına alınmasıdır.<br />\r\n<br />\r\n(2) Defter tutma işlemleri, yazı işleri m&uuml;d&uuml;r&uuml;, yazı işleri personeli veya bilirkişi eliyle yaptırılabilir.<br />\r\n<br />\r\n(3) Defter tutma işleminde, taşınır malların sıra numarası, cinsi, adeti, miktarı, nerede bulundukları, değeri; taşınmaz malların sıra numarası, tapu veya diğer tanım bilgileri, değerleri; bor&ccedil;, alacak ve hakların sıra numarası, niteliği, vadesi ve miktarı, bor&ccedil;lu, alacaklı ve hak sahibinin kimlik bilgileri, kayda ge&ccedil;irilir.<br />\r\n<br />\r\n(4) Defter tutma işlemleri elektronik ortamda yapılır. Zorunlu hallerde fizik&icirc; ortamda yapılan işlemler gecikmeksizin elektronik ortama aktarılır.<br />\r\n<br />\r\n(5) Defter tutma işlemi fizik&icirc; ortamda yapıldığı takdirde defterin her sayfası ayrı ayrı numaralandırılır, imzalanır ve m&uuml;h&uuml;rlenir. Defterin sonu g&ouml;revli personel veya bilirkişi tarafından imzalanır.<br />\r\n<br />\r\n(6) Elektronik veya fizik&icirc; ortamda tutulan defter ilgili h&acirc;kim tarafından g&uuml;venli elektronik imza ya da el ile imzalanarak onaylanır.<br />\r\n<br />\r\nYemin tutanağı<br />\r\n<br />\r\nMADDE 51 &ndash; (1) Yemin tutanağı, mevzuat uyarınca h&acirc;kim huzurunda yemin etmeleri gereken kamu g&ouml;revlileri, bilirkişiler ve diğer kişilerin yapacakları yemine ilişkin d&uuml;zenlenen tutanaktır.<br />\r\n<br />\r\n(2) Yemin tutanağında, mahkemenin veya hukuk dairesinin adı, h&acirc;kimin ve zabit k&acirc;tibinin adı ve soyadı, sicili, imzası; yemin eden kişinin adı ve soyadı, T.C. kimlik numarası, unvanı, imzası; yeminin yapıldığı yer, tarih ve saat; yeminin metni yer alır.<br />\r\n<br />\r\n(3) Yemin tutanağı yaptırılan yeminin niteliğine g&ouml;re taşıması gereken diğer kayıtları da i&ccedil;erir.<br />\r\n<br />\r\n(4) Yemin tutanağı elektronik ortamda d&uuml;zenlenir. Zorunlu hallerde fizik&icirc; ortamda yapılan işlemler gecikmeksizin elektronik ortama aktarılır.<br />\r\n<br />\r\n&Uuml;&Ccedil;&Uuml;NC&Uuml; KISIM<br />\r\n<br />\r\n&Ccedil;eşitli H&uuml;k&uuml;mler<br />\r\n<br />\r\nTebligat işlemleri<br />\r\n<br />\r\nMADDE 52 &ndash; (1) Tebligat işlemleri 11/02/1959 tarihli ve 7201 sayılı Tebligat Kanunu ile Tebligat Kanunu uyarınca &ccedil;ıkarılan y&ouml;netmeliklere g&ouml;re fizik&icirc; ya da elektronik ortamda yapılır.<br />\r\n<br />\r\nAra kararların yerine getirilmesi<br />\r\n<br />\r\nMADDE 53 &ndash; (1) Ara kararlar gereğince yapılması gerekli işlemler derhal yerine getirilir. Ara kararının yerine getirildiği veya yerine getirilememe nedeni zabıt k&acirc;tibi tarafından tarih belirterek şerh verilir.<br />\r\n<br />\r\nM&uuml;zekkerelerin g&ouml;nderilme şekli<br />\r\n<br />\r\nMADDE 54 &ndash; (1) Mahkemelerce yazılan ve fizik&icirc; ortamda g&ouml;nderilmesi gereken yazılar posta yoluyla ya da m&uuml;başir vasıtasıyla g&ouml;nderilir.<br />\r\n<br />\r\n(2) Bu yazılar, h&acirc;kimin izniyle elden takip amacıyla iş sahibine veya varsa vekiline ya da kanun&icirc; temsilcilerine imza karşılığında teslim edilebilir. Elden teslim edilen yazılar mahkeme m&uuml;hr&uuml;yle m&uuml;h&uuml;rlenir ve bir zarfa konularak ağzı kapatılır.<br />\r\n<br />\r\nTanıkların &ccedil;ağrılması, &uuml;cret ve giderlerinin &ouml;denmesi<br />\r\n<br />\r\nMADDE 55 &ndash; (1) Tanıklara g&ouml;nderilecek davetiyede;<br />\r\n<br />\r\na) Tanığın adı, soyadı ve a&ccedil;ık adresi,<br />\r\n<br />\r\nb) Tarafların ad ve soyadları,<br />\r\n<br />\r\nc) Tanıklık yapacağı konu,<br />\r\n<br />\r\n&ccedil;) Hazır bulunması gereken yer, g&uuml;n ve saat,<br />\r\n<br />\r\nd) Mazeret bildirmeksizin gelmediği takdirde zorla getirileceği, gelmemesinin sebep olduğu giderlere ve miktarı belirtilmek suretiyle Kanunda g&ouml;sterilen disiplin para cezasına h&uuml;kmolunacağı,<br />\r\n<br />\r\ne) Duruşmaya gelmesine rağmen kanun&icirc; bir sebep g&ouml;stermeden tanıklıktan &ccedil;ekinir, yemin etmez veya g&ouml;stermiş olduğu sebep mahkemece kabul edilmemesine rağmen tanıklık yapmaktan &ccedil;ekinirse miktarı belirtilmek suretiyle Kanunda g&ouml;sterilen disiplin para cezasına ve bu y&uuml;zden doğan giderleri &ouml;demesine h&uuml;kmolunacağı,<br />\r\n<br />\r\nf) Bakanlık tarafından hazırlanan tarife gereğince &uuml;cret ve giderlerinin &ouml;deneceği,<br />\r\n<br />\r\nyazılır.<br />\r\n<br />\r\n(2) Tanıklık &uuml;creti ve giderleri, tanıklık g&ouml;revi yerine getirildikten sonra tanığın talebi &uuml;zerine, yazı işleri m&uuml;d&uuml;r&uuml; veya g&ouml;revlendireceği personel tarafından &ouml;denebileceği gibi, tanık tarafından hesap numarası bildirildiği takdirde banka hesabına da aktarılabilir. Aksi halde masrafları i&ccedil;inden alınarak PTT merkez ve işyerleri aracılığıyla adreste &ouml;demeli olarak g&ouml;nderilir.<br />\r\n<br />\r\nH&uuml;km&uuml;n korunması<br />\r\n<br />\r\nMADDE 56 &ndash; (1) Elektronik ortamda hazırlanan h&uuml;k&uuml;m, h&uuml;kme katılan h&acirc;kimler ve zabıt k&acirc;tibi tarafından g&uuml;venli elektronik imza ile imzalanarak UYAP veri tabanında saklanır. Ayrıca h&uuml;km&uuml;n &ccedil;ıktısı h&uuml;kme katılan h&acirc;kimler ve zabıt k&acirc;tibi tarafından imzalanıp mahkeme m&uuml;hr&uuml;yle m&uuml;h&uuml;rlenerek karar kartonunda muhafaza edilir.<br />\r\n<br />\r\nKarar verilmiş dosyalara ilişkin işlemler<br />\r\n<br />\r\nMADDE 57 &ndash; (1) H&uuml;km&uuml;n kesinleşmesinden &ouml;nce davadan feragat, davayı kabul veya sulh halinde, h&acirc;kim dosya &uuml;zerinden bu konuda ek karar verir. Taraflarca kanun yoluna başvurulmuş olsa dahi sırf bu nedenlerle dosya istinaf veya temyiz incelemesine g&ouml;nderilmez.<br />\r\n<br />\r\nİlam ve suretlerin verilmesi, kesinleşme kaydı ile har&ccedil;ların tahsili<br />\r\n<br />\r\nMADDE 58 &ndash; (1) H&acirc;kimin re&rsquo;sen harekete ge&ccedil;tiği haller ile kanunlardaki &ouml;zel h&uuml;k&uuml;mler saklı kalmak kaydıyla taraflardan birinin talebi olmadık&ccedil;a h&uuml;k&uuml;m tebliğe &ccedil;ıkarılmaz. Taraflardan birinin talebi halinde h&uuml;km&uuml;n bir n&uuml;shası makbuz karşılığında talep eden tarafa verilir, bir n&uuml;shası da diğer tarafa tebliğe &ccedil;ıkarılır.<br />\r\n<br />\r\n(2) Taraflardan her birine verilen h&uuml;k&uuml;m n&uuml;shası ilamdır.<br />\r\n<br />\r\n(3) Tarafların elinde bulunan h&uuml;k&uuml;m n&uuml;shalarının farklı olması h&acirc;linde karar kartonundaki esas alınır.<br />\r\n<br />\r\n(4) Taraflar, harcın &ouml;denmiş olup olmamasına bakılmaksızın ilamı her zaman alabilirler.<br />\r\n<br />\r\n(5) Bakiye karar ve ilam harcının &ouml;denmemiş olması, h&uuml;km&uuml;n tebliğe &ccedil;ıkarılmasına, takibe konmasına, kanun yollarına başvurulmasına ve h&uuml;km&uuml;n kesinleştirilmesine engel teşkil etmez.<br />\r\n<br />\r\n(6) H&uuml;km&uuml;n kesinleştiği, ilamın altına veya arkasına yazılıp, tarih ve mahkeme m&uuml;hr&uuml; konmak ve daire başkanı veya h&acirc;kim tarafından imzalanmak suretiyle belirtilir.<br />\r\n<br />\r\n(7) Suretler yazı işleri m&uuml;d&uuml;r&uuml; tarafından aslına uygunluğu belirtilip onaylanarak verilir.<br />\r\n<br />\r\nTeminatların iadesi<br />\r\n<br />\r\nMADDE 59 &ndash; (1) İhtiyati tedbir veya ihtiyati haciz taleplerinde teminat olarak yatırılan para, banka mektubu, tahvil veya benzeri evrakın iadesinin istenmesi halinde yazı işleri m&uuml;d&uuml;r&uuml; teminatın iadesi i&ccedil;in kanun&icirc; şartların ger&ccedil;ekleşip ger&ccedil;ekleşmediğini araştırıp bu hususu belgelendirerek g&ouml;r&uuml;ş&uuml; ile birlikte dosyayı h&acirc;kim ya da daire başkanına sunar. Teminat g&ouml;sterilmesini gerektiren sebeplerin ortadan kalktığının anlaşılması halinde mahkemece, teminatın iadesine karar verilir.<br />\r\n<br />\r\nSes ve g&ouml;r&uuml;nt&uuml; nakledilmesi yoluyla duruşma ic');
INSERT INTO `bilgitbl` (`id`, `bilgibaslik`, `bilgi`) VALUES
(7, 'Hukuk Muhakemeleri Kanunu - 14', '<p>1086 sayılı Kanunun 17 nci maddesinde yer alan şubenin işlemlerinden dolayı merkeze karşı a&ccedil;ılacak davalarda, merkezin bulunduğu yer dışında, şubenin bulunduğu yerde de dava a&ccedil;ılabileceğine ilişkin kurala bu d&uuml;zenlemede de yer verilmiştir. Buna g&ouml;re, bir merkeze bağlı olan şube ile işlem yapan kişi, şube ile yapılan işlemden dolayı merkeze karşı dava a&ccedil;ması gerekirse, bu davayı merkezin bulunduğu yerde a&ccedil;abileceği gibi, ayrıca şube ile işlem yapan kişiye kolaylık olmak &uuml;zere, şubenin bulunduğu yerde de, merkeze karşı dava a&ccedil;ılabilecektir. Ayrıca, 1086 sayılı Kanunun 17 nci maddesinde şubenin yaptığı işlemden dolayı, merkeze karşı ifl&acirc;s davası a&ccedil;ılmak istendiğinde, ifl&acirc;s davasının, şubenin bulunduğu yerde a&ccedil;ılamayacağına ilişkin kısma burada yer verilmemiştir. Zira, ifl&acirc;s davasının, merkezin bulunduğu yerde a&ccedil;ılacağı İcra ve İfl&acirc;s Kanununun 154 &uuml;nc&uuml; maddesinde d&uuml;zenlenmiştir. O nedenle, bir başka şekilde, aynı kurala, tekrar yer verilmemiştir.<br />\r\n<br />\r\nİkinci fıkraya g&ouml;re, &ouml;zel hukuk t&uuml;zel kişilerinin kendi i&ccedil; ilişkilerine y&ouml;nelik olmak &uuml;zere, &ouml;rneğin bir &uuml;yenin veya ortağın diğer bir &uuml;yeye veya ortağa yahut &uuml;yenin veya ortağın merkeze karşı a&ccedil;acağı davalar, merkezin bulunduğu yerde a&ccedil;ılacaktır. Bu t&uuml;r davalarda, merkezin bulunduğu yer<br />\r\nmahkemesinin yetkisi kesindir.</p>\r\n'),
(8, 'Hukuk Muhakemeleri Kanunu - 11', '<p>1086 sayılı Kanunun 11 inci maddesinde yer alan, &ouml;len kimsenin, &ouml;l&uuml;m&uuml;nden &ouml;nceki son yerleşim yeri mahkemesinin yetkisini d&uuml;zenleyen yetki kuralı esas olarak aynen kabul edilmiştir.<br />\r\n<br />\r\nAncak, birinci fıkrada, &ouml;len kimsenin son yerleşim yeri mahkemesinin yetkisinin, burada belirtilen davalar i&ccedil;in kesin yetkili olduğu a&ccedil;ık&ccedil;a belirtilmiştir.&nbsp;<br />\r\n<br />\r\nBirinci fıkranın (a) bendine, 1086 sayılı Kanunun 11 inci maddesinin (1) numaralı bendinde yer almamakla beraber, bu kapsamda yer alması gereken &ouml;l&uuml;me bağlı tasarrufların iptali ve tenkisi ile miras sebebiyle istihkaka ilişkin davalar da eklenmiştir.<br />\r\n<br />\r\n1086 sayılı Kanunun 11 inci maddesinin (2) numaralı bendinde &ldquo;terekenin taksimi kat&icirc;sine kadar tereke aleyhine ik&acirc;me olunan d&acirc;valar&rdquo; ifadesine yer verilmişse de, Tasarıda bu ifade, &ldquo;terekenin kesin paylaşımına kadar miras&ccedil;ılara karşı a&ccedil;ılacak t&uuml;m davalar&rdquo; şeklinde değiştirilmiştir. Zira bilindiği gibi, terekenin t&uuml;zel kişiliği bulunmamaktadır. O nedenle de, davada taraf ehliyeti yoktur. Buna bağlı olarak tereke aleyhine dava a&ccedil;ılamaz. Bu halde de, davanın yine miras&ccedil;ılara karşı a&ccedil;ılması gereklidir. O nedenle, &ldquo;tereke aleyhine ik&acirc;me olunan davalar&rdquo; ifadesinin, &ldquo;miras&ccedil;ılara karşı a&ccedil;ılacak t&uuml;m davalar&rdquo; şeklinde değiştirilmesi uygun g&ouml;r&uuml;lm&uuml;şt&uuml;r. Belirtmek gerekir ki, bu durum, miras&ccedil;ıların iştirak halinde m&uuml;lkiyet kuralları gereğince birlikte hareket etmek zorunda oldukları haller i&ccedil;in ge&ccedil;erli olacaktır. Buna karşılık, miras&ccedil;ıların, m&uuml;teselsilen sorumlu oldukları bir bor&ccedil; s&ouml;z konusu ise bu takdirde, miras&ccedil;ılardan birinin bulunduğu yer mahkemesinde de dava a&ccedil;ılabilecektir.<br />\r\n<br />\r\nMaddenin &uuml;&ccedil;&uuml;nc&uuml; fıkrasında ise 1086 sayılı Kanunun 11 inci maddesinin &uuml;&ccedil;&uuml;nc&uuml; fıkrasından farklı olarak &ldquo;miras&ccedil;ıların her birinin bulunduğu mahal&rdquo; ifadesi değiştirilmiş, &ldquo;miras&ccedil;ılardan her birinin oturduğu yer&rdquo; ifadesine yer verilmiştir. Bu değişiklik yapılırken, fıkrada belirtilen işlemler i&ccedil;in miras&ccedil;ılardan birinin tesad&uuml;fen bulunduğu ge&ccedil;ici bir yer mahkemesinin yetkisi kabul edilmemiş, daha uzun s&uuml;re kaldığı, yerleşim yerinin bulunduğu yahut yerleşme niyeti olmaksızın oturduğu yer mahkemesinin de yetkili olması kabul edilmiştir.</p>\r\n'),
(9, 'Hukuk Muhakemeleri Kanunu - 10', '<p>1086 sayılı Kanunun 10 uncu maddesinde yer alan s&ouml;zleşmelere ilişkin yetki kuralında, davalının veya vekilinin dava tarihinde orada bulunması h&acirc;linde, s&ouml;zleşmenin kurulduğu yerde de dava a&ccedil;ılmasına imk&acirc;n verilmekte idi. Ancak, bu yetki kuralının işlerlik kazanabilmesi i&ccedil;in dava a&ccedil;ılmadan &ouml;nce, davalı ya da vekilinin s&ouml;zleşmenin kurulduğu yerde bulunduğunu araştırıp tespit etmek zorunluluğu vardı. Bu durumu tespit etmek de, davacı a&ccedil;ısından olduk&ccedil;a zordur. O nedenle, s&ouml;zleşmenin yapıldığı yer mahkemesinin yetkisine, uygulama alanı bulamadığından, bu maddede yer verilmemiştir.<br />\r\n<br />\r\n1086 sayılı Kanunun 10 uncu maddesine paralel bir bi&ccedil;imde, s&ouml;zleşmenin ifa yeri mahkemesi &ouml;zel yetkili mahkeme olarak muhafaza edilmiştir...</p>\r\n');
